import React, { useState } from "react";
import StripeCheckout from "react-stripe-checkout";
import './bookingpopup.css';
import './Booking.css';
import Navbar from '../Components/Navbar';

const BookingPopup = ({ message, onClose }) => {
    const [product] = useState({
        name: "Rare Fairy",
        price: 500,
        productBy: "Mano",
    });

    const [formData, setFormData] = useState({
        name: "",
        clothColor: "",
        contactNo: "",
        file: null,
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleFileChange = (e) => {
        setFormData({
            ...formData,
            file: e.target.files[0],
        });
    };

    const makePayment = (token) => {
        const body = {
            token,
            product,
        };
        const headers = {
            "Content-Type": "application/json",
        };

        return fetch("http://localhost:3003/payment", {
            method: "POST",
            headers,
            body: JSON.stringify(body),
        })
        .then((response) => {
            console.log("Response:", response);
            const { status } = response;
            console.log("Status:", status);
        })
        .catch((error) => console.log(error));
    };

    return (
        <div className="popup-box">
            <div className="popup">
                <h2>Success Message</h2>
                <h4>*Thank You for registering! Please check your email for verification.**</h4>
                <button type="button" className="btn1" onClick={onClose}>
                    OK
                </button>
                <StripeCheckout
                    stripeKey="pk_test_51PJFojP7PxWjIVya2Bv5APhoOZxJODD5GkAIoYedJdeC9AwkajgyMixKSxbyRKKPVhPWCScfkFAatKx4ztdPoMnS004kNjDNrJ"
                    token={makePayment}
                    name={product.name}
                    amount={product.price * 100} // Amount in cents
                >
                    <button className="dash" id="dash" type="btn1">
                        Pay Now
                    </button>
                </StripeCheckout>
            </div>
        </div>
    );
};

export default BookingPopup;
