import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import emailjs from "@emailjs/browser";
import "react-toastify/dist/ReactToastify.css";
import "./Dashboard.css";
import "./productadmin.css";
import Admin from "../Pages/Admin";
import customer02 from "../Assets/customer02.jpeg";
import Modal from "../Components/Modal";
import ProductForm from "./Addproduct";
import './userdetails.css';
import './Admin.css';

const AdminDashboard = () => {
  const [search, setSearch] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [usersCount, setUsersCount] = useState(0);
  const [ordersCount, setOrdersCount] = useState(0);
  const [productsCount, setProductsCount] = useState(0);
  const [orders, setOrders] = useState([]);
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showEmailForm, setShowEmailForm] = useState(false);
  const form = useRef();

  const [selectedUser, setSelectedUser] = useState(null);
  const [editedName, setEditedName] = useState('');
  const [editedEmail, setEditedEmail] = useState('');
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [isEditConfirmationOpen, setIsEditConfirmationOpen] = useState(false);
  const [isDeleteConfirmationOpen, setIsDeleteConfirmationOpen] = useState(false);

  useEffect(() => {
    fetchUsersCount();
    fetchOrdersCount();
    fetchProductsCount();
    fetchOrders();
    fetchUsers();
  }, []);

  const fetchUsersCount = async () => {
    try {
      const response = await fetch("http://localhost:3003/user/count");
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      setUsersCount(data.count);
    } catch (error) {
      console.error("Error fetching users count:", error);
    }
  };

  const fetchOrdersCount = async () => {
    try {
      const response = await fetch("http://localhost:3003/orders/count");
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      setOrdersCount(data.count);
    } catch (error) {
      console.error("Error fetching orders count:", error);
    }
  };

  const fetchProductsCount = async () => {
    try {
      const response = await fetch("http://localhost:3003/products/count");
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      setProductsCount(data.count);
    } catch (error) {
      console.error("Error fetching products count:", error);
    }
  };

  const fetchOrders = () => {
    fetch("http://localhost:3003/order/orders")
      .then((response) => response.json())
      .then((data) => {
        setOrders(data);
      })
      .catch((error) => {
        console.error("Error fetching orders:", error);
        setError("Failed to fetch orders. Please try again later.");
      });
  };

  const fetchUsers = () => {
    fetch('http://localhost:3003/user/getUser')
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to fetch users');
        }
        return response.json();
      })
      .then(data => setUsers(data))
      .catch(error => {
        console.error('Error fetching users:', error);
        setError('An error occurred while fetching users. Please try again later.');
      });
  };

  const handleEditClick = (user) => {
    setSelectedUser(user);
    setIsEditConfirmationOpen(true);
  };

  const handleDeleteClick = (user) => {
    setSelectedUser(user);
    setIsDeleteConfirmationOpen(true);
  };

  const handleEditConfirm = () => {
    setEditedName(selectedUser.Name);
    setEditedEmail(selectedUser.Email);
    setIsEditModalOpen(true);
    setIsEditConfirmationOpen(false);
  };

  const handleDeleteConfirm = () => {
    deleteHandler(selectedUser._id);
    setIsDeleteConfirmationOpen(false);
  };

  const handleEditCancel = () => {
    setSelectedUser(null);
    setIsEditConfirmationOpen(false);
  };

  const handleDeleteCancel = () => {
    setSelectedUser(null);
    setIsDeleteConfirmationOpen(false);
  };

  const handleEditSave = (e) => {
    e.preventDefault();
    fetch(`http://localhost:3003/user/updateUser/${selectedUser._id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ Name: editedName, Email: editedEmail })
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to update user');
        }
        setIsEditModalOpen(false);
        fetchUsers();
      })
      .catch(error => {
        console.error('Error updating user:', error);
        setError('An error occurred while updating the user. Please try again later.');
      });
  };

  const deleteHandler = (userId) => {
    fetch(`http://localhost:3003/user/deleteUser/${userId}`, {
      method: 'DELETE'
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to delete user');
        }
        fetchUsers();
      })
      .catch(error => {
        console.error('Error deleting user:', error);
        setError('An error occurred while deleting the user. Please try again later.');
      });
  };

  const handleAccept = (orderId) => {
    fetch(`http://localhost:3003/order/orders/${orderId}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ status: "accepted" }),
    })
      .then((response) => response.json())
      .then((data) => {
        setOrders(orders.map((order) => (order._id === orderId ? data : order)));
        setSelectedOrder(data);
        setShowEmailForm(true);
        toast.success("Order accepted successfully!");
      })
      .catch((error) => {
        console.error("Error accepting order:", error);
        toast.error("Failed to accept the order.");
      });
  };

  const handleReject = (orderId) => {
    fetch(`http://localhost:3003/order/orders/${orderId}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ status: "rejected" }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to reject order");
        }
        return response.json();
      })
      .then((data) => {
        setOrders(orders.filter((order) => order._id !== orderId));
        toast.success("Order rejected successfully!");
      })
      .catch((error) => {
        console.error("Error rejecting order:", error);
        toast.error("Failed to reject the order.");
      });
  };

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_7v4vmve",
        "template_gswksyh",
        form.current,
        "hpy_Ow2NKTCTlRRDq"
      )
      .then(
        () => {
          console.log("Email sent successfully!");
          setShowEmailForm(false);
        },
        (error) => {
          console.log("Failed to send email...", error.text);
        }
      );
  };

  const handleSearchChange = (e) => {
    setSearch(e.target.value);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    toast.success("Logout successfully!");
  };

  const handleShow = () => setShowModal(true);
  const handleClose = () => setShowModal(false);

  return (
    <>
      <div className="dashboard">
        <div className="sidebar">
          <div className="sidebar-header">
            <h2>Rare Fairy</h2>
          </div>
          <ul className="sidebar-menu">
            <li>
              <Link to="/Dashboard">
                <i className="fas fa-tachometer-alt"></i> Dashboard
              </Link>
            </li>
            <li>
              <Link to="/OrderList">
                <i className="fas fa-shopping-cart"></i> Order
              </Link>
            </li>
            <li>
              <Link to="/Admin">
                <i className="fas fa-users"></i> Users
              </Link>
            </li>
            <li>
              <a href="/product">
                <i className="fas fa-box-open"></i> Product{" "}
              </a>
            </li>
            <li>
              <Link to="/payment">
                <i className="fas fa-credit-card"></i> Payment
              </Link>
            </li>
            <li>
              <Link to="/Chart">
                <i className="fas fa-chart-line"></i> Chart
              </Link>
            </li>
            <li>
              <Link to="/Addproduct">
                <i className="fas fa-plus"></i> Add Product
              </Link>
            </li>
            <li>
              <Link to="/" onClick={handleLogout}>
                <i className="fas fa-sign-out-alt"></i> Logout
              </Link>
            </li>
          </ul>
        </div>
        <div className="main-content">
          <div className="header">
            <div className="search-bar">
              <input
                type="text"
                placeholder="Search..."
                value={search}
                onChange={handleSearchChange}
              />
              <button type="submit">
                <i className="fas fa-search"></i>
              </button>
            </div>
            <div className="profile">
              <img src={customer02} alt="Profile" />
            </div>
          </div>
          <div className="stats">
            <div className="stat-item">
              <i className="fas fa-users"></i>
              <div className="stat-info">
                <h3>{usersCount}</h3>
                <p>Users</p>
              </div>
            </div>
            <div className="stat-item">
              <i className="fas fa-shopping-cart"></i>
              <div className="stat-info">
                <h3>{ordersCount}</h3>
                <p>Orders</p>
              </div>
            </div>
            <div className="stat-item">
              <i className="fas fa-box-open"></i>
              <div className="stat-info">
                <h3>{productsCount}</h3>
                <p>Products</p>
              </div>
            </div>
          </div>
          <div className="orders">
            <h2>Orders</h2>
            {error && <div className="error">{error}</div>}
            <table>
              <thead>
                <tr>
                  <th>Order ID</th>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Status</th>
                  <th>Accept</th>
                  <th>Reject</th>
                </tr>
              </thead>
              <tbody>
                {orders.map((order) => (
                  <tr key={order._id}>
                    <td>{order._id}</td>
                    <td>{order.name}</td>
                    <td>{order.email}</td>
                    <td>{order.status}</td>
                    <td>
                      <button onClick={() => handleAccept(order._id)}>
                        Accept
                      </button>
                    </td>
                    <td>
                      <button onClick={() => handleReject(order._id)}>
                        Reject
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          {showEmailForm && (
            <form ref={form} onSubmit={sendEmail}>
              <label>Name</label>
              <input
                type="text"
                name="user_name"
                defaultValue={selectedOrder ? selectedOrder.name : ""}
                readOnly
              />
              <label>Email</label>
              <input
                type="email"
                name="user_email"
                defaultValue={selectedOrder ? selectedOrder.email : ""}
                readOnly
              />
              <label>Message</label>
              <textarea name="message"></textarea>
              <input type="submit" value="Send" />
            </form>
          )}
          <div className="user-table">
            <h2>Users</h2>
            {isEditConfirmationOpen && (
              <div className="confirmation-modal">
                <div className="modal-content">
                  <span className="close" onClick={handleEditCancel}>&times;</span>
                  <h2>Confirm Edit</h2>
                  <p>Are you sure you want to edit this user?</p>
                  <button onClick={handleEditConfirm}>Yes</button>
                  <button onClick={handleEditCancel}>No</button>
                </div>
              </div>
            )}
            {isDeleteConfirmationOpen && (
              <div className="confirmation-modal">
                <div className="modal-content">
                  <span className="close" onClick={handleDeleteCancel}>&times;</span>
                  <h2>Confirm Delete</h2>
                  <p>Are you sure you want to delete this user?</p>
                  <button onClick={handleDeleteConfirm}>Yes</button>
                  <button onClick={handleDeleteCancel}>No</button>
                </div>
              </div>
            )}
            {isEditModalOpen && selectedUser && (
              <div className="edit-modal">
                <div className="modal-content">
                  <span className="close" onClick={() => setIsEditModalOpen(false)}>&times;</span>
                  <h2>Edit User</h2>
                  <form onSubmit={handleEditSave}>
                    <label>Name:</label>
                    <input
                      type="text"
                      value={editedName}
                      onChange={(e) => setEditedName(e.target.value)}
                      required
                    />
                    <label>Email:</label>
                    <input
                      type="email"
                      value={editedEmail}
                      onChange={(e) => setEditedEmail(e.target.value)}
                      required
                    />
                    <button type="submit">Save</button>
                  </form>
                </div>
              </div>
            )}
            {error && <div className="error">Error: {error}</div>}
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Delete</th>
                  <th>Edit</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user) => (
                  <tr key={user._id}>
                    <td>{user.Name}</td>
                    <td>{user.Email}</td>
                    <td>
                      <button className="delete" onClick={() => handleDeleteClick(user)}>Delete</button>
                    </td>
                    <td>
                      <button className="edit" onClick={() => handleEditClick(user)}>Edit</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <Admin />
        </div>
      </div>
      <Modal show={showModal} handleClose={handleClose}>
        <ProductForm />
      </Modal>
      <ToastContainer />
    </>
  );
};

export default AdminDashboard;
