// Card.js
import React, { useState } from 'react';
import './designstype.css'; // Assuming it's a CSS file
import axios from 'axios';

const Card = ({ title }) => {
  const [cardImage, setCardImage] = useState([]);

  const handleClick = async (title) => {
    try {
      const response = await axios.get(`http://localhost:3003/image/showimage/${title}`);
      setCardImage(response.data);
      window.location.href = `/Emproidry?title=${encodeURIComponent(title)}&cardImage=${encodeURIComponent(JSON.stringify(response.data))}`;
    } catch (error) {
      console.error('Error fetching card images:', error);
    }
  }

  return (
    <div className="card">
      <div className="card-content">
        <h2 className="card-title">{title}</h2>
        <button onClick={() => handleClick(title)}>View Images</button>
      </div>
    </div>
  );
};

export default Card;
