import React from 'react';
import Card from '../Components/designstype';
import './Aridesigsncard.css';
import Navbar from '../Components/Navbar';

function App() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
      fetch('http://localhost:3003/image/getImg') 
          .then(response => {
              if (!response.ok) {
                  throw new Error('Network response was not ok');
              }
              return response.json();
          })
          .then(data => {
              setProducts(data);
          })
          .catch(error => {
              console.error('Error fetching products:', error);
          });
  }, []);
  const categoryNames = ["Emproidry", "Bridel", "Salwar Designs", "Legenha designs", "Zardosi designs", "Simple blouse designs"];
  return (
    <>
      <Navbar />

      <div className="App">
        <h1> <span> Products</span></h1>
        <div className="card-container">
        <div>
          {products.length > 0 ? (
              products.map((product) => (
                  <div key={product._id}>
                      <h3>{product.CategoryName}</h3>
                      <p>{product.Description}</p>
                      <img src={product.imageUrl} alt={product.CategoryName} />
                  </div>
              ))
          ) : (
              <p>No products available.</p>
          )}
      </div>
          {categoryNames.map((category, index) => (
            <Card
              key={index}
              title={category}
              content={`This is some sample content for ${category}.`}
              imageUrl={`https://via.placeholder.com/150?text=${category}`} 


            />


          ))}
        </div>
      </div>
    </>
  );
}



export default App;
