import React, { useState } from "react";
import { toast } from "react-toastify";
import { Link } from "react-router-dom";
import "./Dashboard.css";
import Admin from "../Pages/Admin";
import customer02 from "../Assets/customer02.jpeg";
import Modal from '../Components/Modal'; // Make sure this path is correct
import ProductForm from './ProductForm'; // Make sure this path is correct

const AdminDashboard = () => {
  const [search, setSearch] = useState("");
  // const [loggedIn, setLoggedIn] = useState(false); // Remove this line if not used
  const [showModal, setShowModal] = useState(false);

  const handleSearchChange = (e) => {
    setSearch(e.target.value);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    toast.success("Logout successfully!");
  };

  const handleShow = () => setShowModal(true);
  const handleClose = () => setShowModal(false);

  return (
    <>
      <div className="dashboard">
        <div className="sidebar">
          <div className="sidebar-header">
            <h2>Rare Fairy</h2>
          </div>
          <ul className="sidebar-menu">
            <li>
              <Link to="#">
                <i className="fas fa-tachometer-alt"></i> Dashboard
              </Link>
            </li>
            <li>
              <Link to="/Admin">
                <i className="fas fa-users"></i> Users
              </Link>
            </li>
            <li>
              <Link to="#">
                <i className="fas fa-cogs"></i> Settings
              </Link>
            </li>
            <li>
              <Link to="/product">
                <i className="fas fa-home"></i> Home
              </Link>
            </li>
            <li>
              <Link to="/payment">
                <i className="fas fa-credit-card"></i> Payment
              </Link>
            </li>
            <li>
              <Link className="a" to="/login" onClick={handleLogout}>
                <i className="fas fa-sign-out-alt"></i> Logout
              </Link>
            </li>
          </ul>
        </div>
        <div className="main-content">
          <div className="header">
            <div className="header-left">
              <h1>Welcome to Admin</h1>
            </div>
            <div className="header-right">
              <div className="search-bar">
                <input
                  type="text"
                  placeholder="Search..."
                  value={search}
                  onChange={handleSearchChange}
                />
              </div>
              <div className="notification">
                <i className="fas fa-bell"></i>
              </div>
              <div className="profile">
                <img src={customer02} alt="Profile" />
                <span>Manojini Suthakar</span>
              </div>
            </div>
          </div>
          <div className="content">
            <div className="card-dash">
              <h3>Users</h3>
              <p>Number of active users: 09</p>
            </div>
            <div className="card-dash">
              <h3>Order</h3>
              <p>Number of orders: 12</p>
            </div>
            <div className="card-dash">
              <button onClick={handleShow}>
                <h3>Add product</h3>
              </button>
            </div>
          </div>
          <Admin />
        </div>
      </div>
      <Modal show={showModal} handleClose={handleClose}>
        <Add />
      </Modal>
    </>
  );
};

export default AdminDashboard;
