import './userdetails.css';
import './Admin.css';
import React, { useState, useEffect } from 'react';

const UserTable = () => {
    const [users, setUsers] = useState([]);
    const [error, setError] = useState('');
    const [selectedUser, setSelectedUser] = useState(null);
    const [editedName, setEditedName] = useState('');
    const [editedEmail, setEditedEmail] = useState('');
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const [isAddModalOpen, setIsAddModalOpen] = useState(false);
    const [newUserName, setNewUserName] = useState('');
    const [newUserEmail, setNewUserEmail] = useState('');

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = () => {
        fetch('http://localhost:3003/user/getuser')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to fetch users');
                }
                return response.json();
            })
            .then(data => setUsers(data))
            .catch(error => {
                console.error('Error fetching users:', error);
                setError('An error occurred while fetching users. Please try again later.');
            });
    };

    // const handleDeleteClick = (userId) => {
    //     fetch(`http://localhost:3003/user/deleteUser/${userId}`, {
    //         method: 'DELETE'
    //     })
    //     .then(response => {
    //         if (!response.ok) {
    //             throw new Error('Failed to delete user');
    //         }
    //         fetchUsers();
    //     })
    //     .catch(error => {
    //         console.error('Error deleting user:', error);
    //         setError('An error occurred while deleting the user. Please try again later.');
    //     });
    // };
    const handleDeleteClick = (userId) => {
        console.log("User ID to be deleted:", userId); // Add this line
        fetch(`http://localhost:3003/user/deleteUser/${userId}`, {
            method: 'DELETE'
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to delete user');
                }
                fetchUsers();
            })
            .catch(error => {
                console.error('Error deleting user:', error);
                setError('An error occurred while deleting the user. Please try again later.');
            });
    };


    const handleEditClick = (user) => {
        setSelectedUser(user);
        setEditedName(user.Name);
        setEditedEmail(user.Email);
        setIsEditModalOpen(true);
    };

    const handleEditSave = (e) => {
        e.preventDefault();
        fetch(`http://localhost:3003/user/updateUser/${selectedUser.UserId}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ Name: editedName, Email: editedEmail })
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Failed to update user');
            }
            setIsEditModalOpen(false);
            fetchUsers();
        })
        .catch(error => {
            console.error('Error updating user:', error);
            setError('An error occurred while updating the user. Please try again later.');
        });
    };

    return (
        <div className="container-box">
            {isEditModalOpen && selectedUser && (
                <div className="edit-modal">
                    <div className="modal-content">
                        <span className="close" onClick={() => setIsEditModalOpen(false)}>&times;</span>
                        <h2>Edit User</h2>
                        <form onSubmit={handleEditSave}>
                            <label>Name:</label>
                            <input
                                type="text"
                                value={editedName}
                                onChange={(e) => setEditedName(e.target.value)}
                                required
                            />
                            <label>Email:</label>
                            <input
                                type="email"
                                value={editedEmail}
                                onChange={(e) => setEditedEmail(e.target.value)}
                                required
                            />
                            <button type="submit">Save</button>
                        </form>
                    </div>
                </div>
            )}

            {error && <div className="error">Error: {error}</div>}
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Delete</th>
                        <th>Edit</th>
                    </tr>
                </thead>
                <tbody>
                {users.map((user) => (
    <tr key={user._id}>
        <td>{user.Name}</td>
        <td>{user.Email}</td>
        <td>
            <button className="delete" onClick={() => handleDeleteClick(user._id)}>Delete</button>
        </td>
        <td>
            <button className="edit" onClick={() => handleEditClick(user)}>Edit</button>
        </td>
    </tr>
))}

                </tbody>
            </table>
        </div>
    );
};

export default UserTable;
