import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Image, Transformation, CloudinaryContext } from 'cloudinary-react';
import Modal from '../Components/Modal'; // Assuming Modal.js is in the correct directory
import './productadmin.css';

function Admin() {
    const [products, setProducts] = useState([]);
    const [error, setError] = useState(null);
    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [isEditConfirmationOpen, setEditConfirmationOpen] = useState(false);
    const [isDeleteConfirmationOpen, setDeleteConfirmationOpen] = useState(false);
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const [editedCategoryName, setEditedCategoryName] = useState('');
    const [editedProductName, setEditedProductName] = useState('');
    const [editedDescription, setEditedDescription] = useState('');

    useEffect(() => {
        axios.get('http://localhost:3003/image/getImg')
            .then(response => setProducts(response.data))
            .catch(error => {
                console.error('Error fetching products:', error);
                setError('An error occurred while fetching products. Please try again later.');
            });
    }, []);

    const handleEdit = (product) => {
        setSelectedProduct(product);
        setEditConfirmationOpen(true);
    };

    const handleEditConfirm = () => {
        setIsEditModalOpen(true);
        setEditConfirmationOpen(false);
    };

    const handleEditCancel = () => {
        setEditConfirmationOpen(false);
    };

    const handleEditSave = (e) => {
        e.preventDefault();
        // Your logic to save edited product details
        setIsEditModalOpen(false); // Close the edit modal after saving
    };

    const handleDelete = (productId) => {
        setDeleteConfirmationOpen(true);
    };

    const handleDeleteConfirm = () => {
        // Your logic to confirm and delete product
        setDeleteConfirmationOpen(false); // Close the delete confirmation modal
    };

    const handleDeleteCancel = () => {
        setDeleteConfirmationOpen(false);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setSelectedProduct({
            ...selectedProduct,
            [name]: value
        });
    };

    return (
        <div>
            {error ? (
                <div className="error">Error: {error}</div>
            ) : (
                <table>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Category Name</th>
                            <th>Product Name</th>
                            <th>Description</th>
                            <th>Image</th>
                            <th>Edit</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map(product => (
                            <tr key={product._id}>
                                <td>{product._id}</td>
                                <td>{product.CategoryName}</td>
                                <td>{product.ProductName}</td>
                                <td>{product.Description}</td>
                                <td>
                                    <CloudinaryContext cloudName="your_cloud_name">
                                        <Image publicId={product.imageUrl} alt={product.ProductName} style={{ height:"50px",width:"50px"}}>
                                            <Transformation width="50" crop="scale" />
                                        </Image>
                                    </CloudinaryContext>
                                </td>
                                <td>
                                    <button className="edit" onClick={() => handleEdit(product)}>Edit</button>
                                </td>
                                <td>
                                    <button className="delete" onClick={() => handleDelete(product._id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}

            <Modal isOpen={modalIsOpen} onClose={() => setModalIsOpen(false)}>
                <h2>Edit Product</h2>
                {selectedProduct && (
                    <form onSubmit={handleEditSave}>
                        <label>
                            Category Name:
                            <input
                                type="text"
                                name="CategoryName"
                                value={editedCategoryName}
                                onChange={(e) => setEditedCategoryName(e.target.value)}
                            />
                        </label>
                        <label>
                            Product Name:
                            <input
                                type="text"
                                name="ProductName"
                                value={editedProductName}
                                onChange={(e) => setEditedProductName(e.target.value)}
                            />
                        </label>
                        <label>
                            Description:
                            <input
                                type="text"
                                name="Description"
                                value={editedDescription}
                                onChange={(e) => setEditedDescription(e.target.value)}
                            />
                        </label>
                        <button type="submit">Save</button>
                        <button type="button" onClick={() => setModalIsOpen(false)}>Cancel</button>
                    </form>
                )}
            </Modal>

            <div className="container-box">
                {isEditConfirmationOpen && (
                    <div className="confirmation-modal">
                        <div className="modal-content">
                            <span className="close" onClick={handleEditCancel}>&times;</span>
                            <h2>Confirm Edit</h2>
                            <p>Are you sure you want to edit this product?</p>
                            <button onClick={handleEditConfirm}>Yes</button>
                            <button onClick={handleEditCancel}>No</button>
                        </div>
                    </div>
                )}
                {isDeleteConfirmationOpen && (
                    <div className="confirmation-modal">
                        <div className="modal-content">
                            <span className="close" onClick={handleDeleteCancel}>&times;</span>
                            <h2>Confirm Delete</h2>
                            <p>Are you sure you want to delete this product?</p>
                            <button onClick={handleDeleteConfirm}>Yes</button>
                            <button onClick={handleDeleteCancel}>No</button>
                        </div>
                    </div>
                )}
                {isEditModalOpen && selectedProduct && (
                    <div className="edit-modal">
                        <div className="modal-content">
                            <span className="close" onClick={() => setIsEditModalOpen(false)}>&times;</span>
                            <h2>Edit Product</h2>
                            <form onSubmit={handleEditSave}>
                                <label>Category Name:</label>
                                <input
                                    type="text"
                                    value={editedCategoryName}
                                    onChange={(e) => setEditedCategoryName(e.target.value)}
                                />
                                <label>Product Name:</label>
                                <input
                                    type="text"
                                    value={editedProductName}
                                    onChange={(e) => setEditedProductName(e.target.value)}
                                />
                                <label>Description:</label>
                                <input
                                    type="text"
                                    value={editedDescription}
                                    onChange={(e) => setEditedDescription(e.target.value)}
                                />
                                <button type="submit">Save</button>
                            </form>
                        </div>
                    </div>
                )}
            </div>

      <div className="dashboard">
        <div className="sidebar">
          <div className="sidebar-header">
            <h2>Rare Fairy</h2>
          </div>
          <ul className="sidebar-menu">
            <li>
              <Link to="/Dashboard">
                <i className="fas fa-tachometer-alt"></i> Dashboard
              </Link>
            </li>
            <li>
    <Link to="/OrderList">
        <i className="fas fa-shopping-cart"></i> Order
    </Link>
</li>

            <li>
              <Link to="/Admin">
                <i className="fas fa-users"></i> Users
              </Link>
            </li>
            <li>
              <a href="/product">
                 <i className="fas fa-box-open"></i> Product  </a>
           </li>

            <li>
              <Link to="/payment">
                <i className="fas fa-credit-card"></i> Payment
              </Link>
            </li>
            <li>
              <Link className="a" to="/login" onClick={handleLogout}>
                <i className="fas fa-sign-out-alt"></i> Logout
              </Link>
            </li>
          </ul>
        </div>
        <div className="main-content">
          <div className="header">
            <div className="header-left">
              <h1>Welcome to Admin</h1>
            </div>
            <div className="header-right">
              <div className="search-bar">
                <input
                  type="text"
                  placeholder="Search..."
                  value={search}
                  onChange={handleSearchChange}
                />
              </div>
              <div className="notification">
                <i className="fas fa-bell"></i>
              </div>
              <div className="profile">
                <img src={customer02} alt="Profile" />
                <span>Manojini Suthakar</span>
              </div>
            </div>
          </div>
          <div className="content">
            <div className="card-dash">
              <h3>Users</h3>
              <p>Number of active users: 09</p>
            </div>
            <div className="card-dash">
              <h3>Order</h3>
              <p>Number of orders: 12</p>
            </div>
            <div className="card-dash">


				<Link to="/Addproduct" onClick={handleShow}>
                <h3>Add product</h3>
                <p>Number of products:15</p>
              </Link>

            </div>
          </div>
          {/* <Admin /> */}
        </div>
      </div>
      <Modal show={showModal} handleClose={handleClose}>
        <ProductForm />
      </Modal>
        </div>
    );
}

export default Admin;
