import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Image, Transformation, CloudinaryContext } from 'cloudinary-react';
import Modal from '../Components/Modal';
import './productadmin.css';

function Admin() {
    const [products, setProducts] = useState([]);
    const [error, setError] = useState(null);
    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [isEditConfirmationOpen, setEditConfirmationOpen] = useState(false);
    const [isDeleteConfirmationOpen, setDeleteConfirmationOpen] = useState(false);
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const perPage = 10; // Number of products per page

    useEffect(() => {
        fetchProducts();
    }, [currentPage]); // Reload products when currentPage changes

    const fetchProducts = async () => {
        try {
            const response = await axios.get(`http://localhost:3003/image/getImg?page=${currentPage}&limit=${perPage}`);
            setProducts(response.data.products);
            setTotalPages(Math.ceil(response.data.total / perPage));
        } catch (error) {
            console.error('Error fetching products:', error);
            setError('An error occurred while fetching products. Please try again later.');
        }
    };

    const handleEdit = (product) => {
        setSelectedProduct(product);
        setEditConfirmationOpen(true);
    };

    const handleEditConfirm = () => {
        setIsEditModalOpen(true);
        setEditConfirmationOpen(false);
    };

    const handleEditCancel = () => {
        setEditConfirmationOpen(false);
    };

    const handleEditSave = (e) => {
        e.preventDefault();
        // Logic to save edited product details
        // Update the products array with the edited product
        const updatedProducts = products.map((product) =>
            product._id === selectedProduct._id ? selectedProduct : product
        );
        setProducts(updatedProducts);
        setIsEditModalOpen(false); // Close the edit modal after saving
    };

    const handleDelete = (productId) => {
        setSelectedProduct(products.find((product) => product._id === productId));
        setDeleteConfirmationOpen(true);
    };

    const handleDeleteConfirm = () => {
        // Logic to confirm and delete product
        setProducts(products.filter((product) => product._id !== selectedProduct._id));
        setDeleteConfirmationOpen(false); // Close the delete confirmation modal
    };

    const handleDeleteCancel = () => {
        setDeleteConfirmationOpen(false);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setSelectedProduct({
            ...selectedProduct,
            [name]: value
        });
    };

    const goToPage = (page) => {
        setCurrentPage(page);
    };

    return (
        <>
            <div>
                <h1 className="order">Rare Fairy Product</h1>
                {error ? (
                    <div className="error">Error: {error}</div>
                ) : (
                    <>
                        <table>
                            <thead>
                                <tr>
                                    <th>Category Name</th>
                                    <th>Product Name</th>
                                    <th>Description</th>
                                    <th>Image</th>
                                    <th>Edit</th>
                                    <th>Delete</th>
                                </tr>
                            </thead>
                            <tbody>
                                {products.map((product) => (
                                    <tr key={product._id}>
                                        <td>{product.CategoryName}</td>
                                        <td>{product.ProductName}</td>
                                        <td>{product.Description}</td>
                                        <td>
                                            <CloudinaryContext cloudName="your_cloud_name">
                                                <Image
                                                    publicId={product.imageUrl}
                                                    alt={product.ProductName}
                                                    style={{ height: '60px', width: '60px', borderRadius: '30%' }}
                                                >
                                                    <Transformation width="50" crop="scale" />
                                                </Image>
                                            </CloudinaryContext>
                                        </td>
                                        <td>
                                            <button className="edit" onClick={() => handleEdit(product)}>
                                                Edit
                                            </button>
                                        </td>
                                        <td>
                                            <button className="delete" onClick={() => handleDelete(product._id)}>
                                                Delete
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                        <div className="pagination">
                            {Array.from({ length: totalPages }, (_, index) => (
                                <button key={index} onClick={() => goToPage(index + 1)}>
                                    {index + 1}
                                </button>
                            ))}
                        </div>
                    </>
                )}

                <Modal isOpen={modalIsOpen} onClose={() => setModalIsOpen(false)}>
                    <h2>Edit Product</h2>
                    {selectedProduct && (
                        <form onSubmit={handleEditSave}>
                            <label>
                                Category Name:
                                <input
                                    type="text"
                                    name="CategoryName"
                                    value={selectedProduct.CategoryName}
                                    onChange={handleInputChange}
                                />
                            </label>
                            <label>
                                Product Name:
                                <input
                                    type="text"
                                    name="ProductName"
                                    value={selectedProduct.ProductName}
                                    onChange={handleInputChange}
                                />
                            </label>
                            <label>
                                Description:
                                <input
                                    type="text"
                                    name="Description"
                                    value={selectedProduct.Description}
                                    onChange={handleInputChange}
                                />
                            </label>
                            <button type="submit">Save</button>
                            <button type="button" onClick={() => setModalIsOpen(false)}>
                                Cancel
                            </button>
                        </form>
                    )}
                </Modal>

                <div className="container-box">
                    {/* Confirmation modals */}
                </div>
            </div>
        </>
    );
}

export default Admin;
