import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './productadmin.css';

function Admin() {
    const [cardImage, setCardImage] = useState([]);

    useEffect(() => {
        const fetchCardImage = async () => {
          try {
            const response = await axios.get(`http://localhost:3003/image/showimage/`);
            setCardImage(response.data);
            ;
          } catch (error) {
            console.error('Error fetching card images:', error);
          }
        };

        fetchCardImage();
      }, []);

    return (
        <div>

                <table>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Ari Id</th>
                            <th>Image Id</th>
                            <th>Title</th>
                            <th>Price</th>
                            <th>Edit</th>
                            <th>Delete</th>
                            <th>Add</th>
                        </tr>
                    </thead>
                    <tbody>
                        {cardImage.map(cardImage => (
                            <tr key={cardImage._id}>
                                <td>{cardImage._id}</td>
                                <td>{cardImage.AriId}</td>
                                <td>{cardImage.ImageId}</td>
                                <td>{cardImage.Tittle}</td>
                                <td>{cardImage.Price}</td>
                                {/* <td className="button-container">
                                    <button className="edit" type="button" onClick={() => handleEdit(cardImage._id)}>Edit</button>
                                    <button className="delete" type="button" onClick={() => handleDelete(cardImage._id)}>Delete</button>
                                    <button className="add" type="button" onClick={handleAdd}>Add</button>
                                </td> */}
                            </tr>
                        ))}
                    </tbody>
                </table>
        </div>
    );
}

export default Admin;
