// Emproidry.js
import React from 'react';
import { useLocation } from 'react-router-dom';
import Navbar from '../Components/Navbar';
import Footer from '../Components/Footer';

const Emproidry = () => {
  // Access the location state using useLocation hook
  const { state } = useLocation();
  const { title, cardImage } = state;

  return (
    <>
      <Navbar />
      <div className="emp-head">{title}<span> Designs</span></div>
      <div className="container-emp">
        <div className="card-emp">
          {/* Display the title and cardImage data */}
          <h2>{title}</h2>
          <img src={cardImage} alt={title} />
          <p>Description of the design...</p>
          <a href="/Description" className="button">View</a>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Emproidry;
