import React, { useState, useEffect } from 'react';
import './Description.css';
import Navbar from '../Components/Navbar';
import Footer from '../Components/Footer';
import axios from 'axios'; // Import axios for making HTTP requests
import { useParams } from 'react-router-dom'; // Import useParams to get the ID from the URL

const Description = () => {
  const { id } = useParams(); // Get the ID from the URL
  const [image, setImage] = useState(null); // State to hold the image details

  useEffect(() => {
    // Fetch image details by ID from the backend
    const fetchImageDetails = async () => {
      try {
        const response = await axios.get(`http://localhshowAllImage/${id}`); // Replace 'your-backend-url' with your actual backend URL
        setImage(response.data); // Set the image details in the state
      } catch (error) {
        console.error('Error fetching image details:', error);
      }
    };

    fetchImageDetails(); // Call the fetchImageDetails function when the component mounts
  }, [id]); // Include id in the dependency array to fetch image details whenever the ID changes

  return (
    <>
      <Navbar />
      <div className="body">
        <div className="heading-des">
          <h1>Image and <span>Description</span></h1>
          {image && <p>{image.CategoryName}</p>} {/* Display the category name if image details are available */}
        </div>
        <div className="container-des">
          <section className="about2">
            {image && (
              <div className="about2-image"> 
                <img src={image.imageUrl} alt={image.CategoryName} />
                <div className="about-content2">
                  <p>{image.Description}</p>
                  <h3>Price = {image.price}/=</h3>
                  <a href='/Booking'>
                    <button className="dash1" id="dash">Book Now</button>
                  </a>
                </div>
              </div>
            )}
          </section>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Description;
