import React, { useState, useEffect } from 'react';
import './Register.css';
import Navbar from '../Components/Navbar';
import { Link, useNavigate } from 'react-router-dom';

function RegistrationForm() {
  const [fullname, setFullName] = useState('');
  const [username, setUserName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmpassword, setConfirmPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [countdown, setCountdown] = useState(5); // Duration for the success modal in seconds
  const navigate = useNavigate();

  useEffect(() => {
    // Countdown timer for closing the success modal automatically
    let timer;
    if (showSuccessModal && countdown > 0) {
      timer = setTimeout(() => {
        setCountdown(countdown - 1);
      }, 1000);
    } else if (showSuccessModal && countdown === 0) {
      // Close the success modal after the countdown completes
      setShowSuccessModal(false);
      // Redirect to login page after successful registration
      navigate('/login');
    }
    return () => clearTimeout(timer);
  }, [showSuccessModal, countdown, navigate]);

  const validate = () => {
    // Validation logic for form fields
    // Same as before...
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = validate();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    setIsSubmitting(true);
    const result = { UserId: username, Name: fullname, Email: email, Password: password, Role: "user" };

    try {
      const response = await fetch('http://localhost:3003/usercreate/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(result),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setErrors({ apiError: errorData.message });
        setIsSubmitting(false);
        return;
      }

      // Registration successful, show success modal
      setShowSuccessModal(true);
      setCountdown(5); // Reset countdown timer
    } catch (error) {
      // Error handling
      console.error('Error:', error);
      setErrors({ apiError: 'An error occurred. Please try again later.' });
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <Navbar />
      <div className="Register-center">
        <h1>Registration Form</h1>
        <form onSubmit={handleSubmit}>
          {/* Form fields */}
          {/* Same as before... */}

          <input type="submit" value="Register Now" disabled={isSubmitting} />
          {/* Submitting/loading indicator */}
          {isSubmitting && <span className="loading">Submitting...</span>}
          {/* API error message */}
          {errors.apiError && <span className="error">{errors.apiError}</span>}
          {/* Link to popup */}
          <Link to="/Popup"></Link>
        </form>
      </div>

      {/* Success modal */}
      {showSuccessModal && (
        <div className="success-modal">
          <div className="success-modal-content">
            <h2>Registration Successful!</h2>
            <p>Redirecting to login page in {countdown} seconds...</p>
          </div>
        </div>
      )}
    </>
  );
}

export default RegistrationForm;
