import React, { useState, useEffect } from 'react';
import axios from 'axios';
import zardosi3 from '../Assets/zardosi3.jpg';
import bridal from '../Assets/blouse.jpg';
import salwar from '../Assets/salwar.jpeg';
import emproidryblouse from '../Assets/emp1.jpg';
import SalwarImage from '../Assets/salwar2.png';
import bridalimage from '../Assets/bridal3.png';
import salwar4 from '../Assets/salwar5.png';
import Navbar from '../Components/Navbar'; // Ensure this path is correct
import './designstype.css'
function Cards() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetch('http://localhost:3003/Aridesigner/getAri')
            .then(response => response.json())
            .then(data => {
                setProducts(data);
            })
            .catch(error => {
                console.error('Error fetching products:', error);
            });
    }, []);

    return (
        <>
            <div className="body">
                <div className="type-container">
                    <div className="row1">
                        <div className="header1">
                            <h1>Rare Fairy <span>Products</span></h1>
                            <p>Rare Fairy is a clothing store in Vavuniya that offers a wide range of designs. Customers can work directly with our embroidery staff to design their bridal goods, whether it's Aari embroidery, zardozi embroidery, Lehenga, or Readymade designer blouses, till it's the ideal fit. Personalized work is handled by our team, which ensures that it is completed on time for delivery. We pride ourselves on providing high-quality, low-cost products to our customers. Rare Fairy is a reputable and fashionable women's clothing store that offers high-quality items at reasonable prices, as well as the most up-to-date and appealing designs.</p>
                        </div>
                        <div className="type-content">
                            {products.map(product => (
                                <div className="card2" key={product.AriId}>
                                    <img src={product.ImageId} alt={product.Title} />
                                    <h4>{product.Type}</h4>
                                    <div className="fake">
                                        <a href="">
                                            <button>Click here</button>
                                        </a>
                                    </div>
                                </div>
                            ))}
                        </div>


                    </div>
                </div>
            </div>
        </>
    );
}

export default Cards;
