import React, { useState } from 'react';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const OrderForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        contactNumber: '',
        address: '',
        deadline: ''
    });

    const [showSuccessModal, setShowSuccessModal] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmitOrder = async () => {
        try {
            const response = await fetch("http://localhost:3003/order/orders", {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData)
            });

            const data = await response.json();

            if (response.ok) {
                setShowSuccessModal(true);
                toast.success("Order submitted successfully!");
            } else {
                console.error("Order submission failed:", data);
                toast.error("Order submission failed. Please try again.");
            }
        } catch (error) {
            console.error("Error:", error);
            toast.error("An error occurred. Please try again.");
        }
    };

    return (
        <div>
            <h1>Submit Order</h1>
            <form onSubmit={(e) => { e.preventDefault(); handleSubmitOrder(); }}>
                <div>
                    <label>Name:</label>
                    <input type="text" name="name" value={formData.name} onChange={handleChange} />
                </div>
                <div>
                    <label>Contact Number:</label>
                    <input type="text" name="contactNumber" value={formData.contactNumber} onChange={handleChange} />
                </div>
                <div>
                    <label>Address:</label>
                    <input type="text" name="address" value={formData.address} onChange={handleChange} />
                </div>
                <div>
                    <label>Deadline:</label>
                    <input type="text" name="deadline" value={formData.deadline} onChange={handleChange} />
                </div>
                <button type="submit">Submit Order</button>
            </form>
            {showSuccessModal && <div>Order Submitted Successfully!</div>}
        </div>
    );
};

export default OrderForm;
