import React from 'react';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Login.css'; 


function LoginForm() {
  const [Email,setEmail] = useState('')
  const[password,setPassword] = useState('')
  const navigate = useNavigate()

const handleSubmit = (e) => {
  e.preventDefault()
  axios.post ('http://localhost:3003/login',{Email,Password})
  .then (result => {
    console.log(result)
    if(result.data === "succes"){
      navigate('/Home')
    }
  })
  .catch (err => console.log(err))
}

  return (
    <div className='log'>
    <div className="Login-center">
      <h1>Login</h1>
      <form method="post" onSubmit={{handleSubmit}}>
        <div className="txt_field">
          <input type="text" required value={data.Email} onChange={(e)=> setData({...data,Email:e.target.value})} />
          <span></span>
          <label>Username</label>
        </div>
        <div className="txt_field">
          <input type="password" required value={data.Password} onChange={(e)=> setData({...data,password:e.target.value})} />
          <span></span>
          <label>Password</label>
        </div>
        <div className="pass">Forgot Password?</div>
        <input type="submit" value="Login" />
        <div className="signup_link">
          Not a member? <a href="Register">Signup</a>
        </div>
      </form>
    </div>
    </div>
  );
}

export default LoginForm;

