import React from 'react';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Login.css'; 
import Navbar from '../Components/Navbar';
// import Footer from '../Components/Footer';

function LoginForm() {
  const [Email,setEmail] = useState('')
  const[password,setPassword] = useState('')
  const navigate = useNavigate()

// const handleSubmit = (e) => {
//   e.preventDefault()
//   axios.post ('http://localhost:3003/usercreate/login',{Email,password})
//   .then (result => {
//     console.log(result)
//     if(result.data === "succes"){
//       navigate('/Home')
//     }
//   })
//   .catch (err => console.log(err))
// }
const handleSubmit = async (e) => {
  e.preventDefault();
  // Check if email and password are not empty
  if (!Email || !password) {
      alert('Please enter both email and password.');
      return;
  }
  try {
      // Send login request to the server
      const response = await axios.post('http://localhost:3002/user/login', { Email, password });
      const data = response.data;
      console.log('Response Data:', response.data); // Log the response data
      if (data) {
          // Redirect user based on role
          if (data === 'e') {
              alert('Admin login successful'); // Add alert for debugging
              navigate('/admin');
          } else {
              alert('User login successful'); // Add alert for debugging
              navigate('/');
          }
      } else {
          // Display error message to the user
          alert('Login failed. Please check your credentials.');
      }
  } catch (error) {
      // Handle error
      console.error('Error logging in:', error);
      // Display error message to the user
      alert('An error occurred while logging in. Please try again later.');
  }
};


  return (
    <>
    <Navbar/>
    <div className='log'>
    <div className="Login-center">
      <h1>Login</h1>
      <form method="post" onSubmit={{handleSubmit}}>
        <div className="txt_field">

          <input type="text" required value={Email} onChange={(e)=> setEmail(e.target.value)} />
          <span></span>
          <label>Email</label>
        </div>

        <div className="txt_field">
          <input type="password" required value={password} onChange={(e)=> setPassword(e.target.value)} />
          <span></span>
          <label>Password</label>
        </div>
        <div className="pass">Forgot Password?</div>
        <input type="submit" value="Login" />
        <div className="signup_link">
          Not a member? <a href="Register">Signup</a>
        </div>
      </form>
    </div>
    </div>

    </>
  );
}

export default LoginForm;

