import React, { useState } from 'react';
import './designstype.css'; // Assuming it's a CSS file
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Card = ({ title, content, imageUrl }) => {
  const [cardImage, setCardImage] = useState([]);
  const navigate =useNavigate();

  const handleClick = async (title, cardImage) => {
    try {
      const response = await axios.get(`http://localhost:3003/image/showimage/${title}`);
      setCardImage(response.data);
      console.log(response.data)
    } catch (error) {
      console.error('Error fetching card images:', error);
    }
  }

  return (
    <div className="card">
      {imageUrl && <img src={imageUrl} alt={title} className="card-image" />}
      <div className="card-content">
        <h2 className="card-title">{title}</h2>
        <p className="card-text">{content}</p>
        <button onClick={() => handleClick(title, cardImage)}>View Images</button>
      </div>
    </div>
  );
};

export default Card;
