import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './Addproduct.css';

function ProductForm() {
  const [productName, setProductName] = useState('');
  const [description, setDescription] = useState('');
  const [categoryName, setCategoryName] = useState('');
  const [photo, setPhoto] = useState(null);
  const [errors, setErrors] = useState({});

  const validate = () => {
    const newErrors = {};
    if (!productName) newErrors.productName = "Product Name is required";
    if (!description) newErrors.description = "Description is required";
    if (!categoryName) newErrors.categoryName = "Category Name is required";
    if (!photo) newErrors.photo = "Photo is required";
    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = validate();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    const formData = new FormData();
    formData.append('ProductName', productName);
    formData.append('Description', description);
    formData.append('CategoryName', categoryName);
    formData.append('photo', photo);

    try {
      const response = await fetch('http://localhost:3003/image/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorText = await response.text(); // Get error text for debugging
        throw new Error(`HTTP error! Status: ${response.status}, Message: ${errorText}`);
      }

      const data = await response.json();
      toast.success('Product added successfully!');

      // Clear the form
      setProductName('');
      setDescription('');
      setCategoryName('');
      setPhoto(null);
      setErrors({});

      // Fetch the updated list of products if necessary
      // fetchProducts(); // Uncomment if defined elsewhere
    } catch (error) {
      console.error('Error:', error);
      toast.error('Failed to add product. Please try again.');
    }
  };

  return (
    <div className="ProductForm">
      <ToastContainer />
      <span>Add Product</span>
      <form onSubmit={handleSubmit}>
      <div className="txt_field">
  <select 
    value={categoryName} 
    onChange={(e) => setCategoryName(e.target.value)} 
    required
  >
    <option value="">Select Category</option>
    <option value="Category1">Category 1</option>
    <option value="Category2">Category 2</option>
    {/* Add more options as needed */}
  </select>
  <span></span>
  <label>Category Name</label>
  {errors.categoryName && <span className="error">{errors.categoryName}</span>}
</div>

        <div className="txt_field">
          <input 
            type="text" 
            required 
            value={productName} 
            onChange={(e) => setProductName(e.target.value)} 
          />
          <span></span>
          <label>Product Name</label>
          {errors.productName && <span className="error">{errors.productName}</span>}
        </div>
        <div className="txt_field">
          <input 
            type="text" 
            required 
            value={description} 
            onChange={(e) => setDescription(e.target.value)} 
          />
          <span></span>
          <label>Description</label>
          {errors.description && <span className="error">{errors.description}</span>}
        </div>
        <div className="txt_field">
          <input 
            type="file" 
            name="photo"
            required 
            onChange={(e) => setPhoto(e.target.files[0])} 
          />
          <span></span>
          <label>Photo</label>
          {errors.photo && <span className="error">{errors.photo}</span>}
        </div>
        <input className='ap' type="submit" value="Add Product" />
      </form>
    </div>
  );
}

export default ProductForm;
