import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './productadmin.css';

function Admin() {
    const [users, setUsers] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:3003/image/getImg')
            .then(response => setUsers(response.data))
            .catch(error => {
                console.error('Error fetching users:', error);
                setError('An error occurred while fetching users. Please try again later.');
            });
    }, []);

    const handleEdit = (userId) => {
        console.log('Edit user with ID:', userId);
    };

    const handleDelete = (userId) => {
        console.log('Delete user with ID:', userId);
        axios.delete(`http://localhost:3003/Aridesigner/${userId}`)
            .then(() => {
                setUsers(users.filter(user => user._id !== userId));
            })
            .catch(error => {
                console.error('Error deleting user:', error);
                setError('An error occurred while deleting user. Please try again later.');
            });
    };

    return (
        <div>
            {error ? (
                <div className="error">Error: {error}</div>
            ) : (
                <table>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Category Name</th>
                            <th>Product Name</th>
                            <th>Description</th>
                            <th>Edit</th>
                            <th>  Delete </th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user._id}>
                                <td>{user.ImageId}</td>
                                <td>{user.CategoryName}</td>
                                <td>{user.ProductName}</td>
                                <td>{user.Description}</td>
                                <td><img src={user.ImageId} alt={user.ProductName} style={{ maxWidth: '100px' }}/></td>
                                <td className="table-buttons">
                                    <button className="edit" type="button" onClick={() => handleEdit(user._id)}>Edit</button>
                                    <button className="delete" type="button" onClick={() => handleDelete(user._id)}>Delete</button>
                                </td>

                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default Admin;
