import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import "./productadmin.css";
import emailjs from '@emailjs/browser';

function OrderList() {
    const [orders, setOrders] = useState([]);
    const [error, setError] = useState(null);
    const [selectedOrder, setSelectedOrder] = useState(null);
    const [isEmailFormOpen, setIsEmailFormOpen] = useState(false);
    const form = useRef();

    useEffect(() => {
        // Fetch orders when component mounts
        axios
            .get("http://localhost:3003/order/orders")
            .then((response) => {
                setOrders(response.data); // Set fetched orders to state
            })
            .catch((error) => {
                console.error("Error fetching orders:", error);
                setError("Failed to fetch orders. Please try again later.");
            });
    }, []);

    const handleAccept = (orderId) => {
        axios
            .patch(`http://localhost:3003/order/orders/${orderId}`, {
                status: "accepted",
            })
            .then((response) => {
                console.log("Order accepted:", response.data);
                setOrders(
                    orders.map((order) => (order._id === orderId ? response.data : order))
                );
                sendNotification(response.data);
                setSelectedOrder(response.data); // Set the selected order for email form
                setIsEmailFormOpen(true); // Open the email form after accepting the order
            })
            .catch((error) => {
                console.error("Error accepting order:", error);
            });
    };

    const handleReject = (orderId) => {
        axios
            .patch(`http://localhost:3003/order/orders/${orderId}`, {
                status: "rejected",
            })
            .then((response) => {
                console.log("Order rejected:", response.data);
                setOrders(
                    orders.map((order) => (order._id === orderId ? response.data : order))
                );
                sendNotification(response.data);
            })
            .catch((error) => {
                console.error("Error rejecting order:", error);
            });
    };

    const sendNotification = (order) => {
        axios
            .post("http://localhost:3003/order/notify", order)
            .then((response) => {
                console.log("Notification sent:", response.data);
            })
            .catch((error) => {
                console.error("Error sending notification:", error);
            });
    };

    const sendEmail = (e) => {
        e.preventDefault();

        emailjs
            .sendForm('service_7v4vmve', 'template_gswksyh', form.current, 'hpy_Ow2NKTCTlRRDq')
            .then(
                () => {
                    console.log('SUCCESS!');
                    setIsEmailFormOpen(false); // Close the email form after sending the email
                },
                (error) => {
                    console.log('FAILED...', error.text);
                },
            );
    };

    return (
        <div>
            <h1 className="order">Order List</h1>
            {error && <p>{error}</p>}
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Contact Number</th>
                        <th>Address</th>
                        <th>Deadline</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map((order) => (
                        <tr key={order._id}>
                            <td>{order.name}</td>
                            <td>{order.email}</td>
                            <td>{order.contactNumber}</td>
                            <td>{order.address}</td>
                            <td>{new Date(order.deadline).toLocaleDateString()}</td>
                            <td>
                                <button className="accept" onClick={() => handleAccept(order._id)}>
                                    Accept
                                </button>
                                <button className="reject" onClick={() => handleReject(order._id)}>
                                    Reject
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {isEmailFormOpen && selectedOrder && (
                <form ref={form} onSubmit={sendEmail}>
                    <h2>Send Email</h2>
                    <label>Name</label>
                    <input type="text" name="user_name" defaultValue={selectedOrder.name} />
                    <label>Email</label>
                    <input type="email" name="user_email" defaultValue={selectedOrder.email} />
                    <label>Message</label>
                    <textarea name="message" />
                    <input type="submit" value="Send" />
                </form>
            )}
        </div>
    );
}

export default OrderList;
