// UserTable.js

import './userdetails.css';
import React, { useState, useEffect } from 'react';

const UserTable = () => {
    const [users, setUsers] = useState([]);
    const [error, setError] = useState('');
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const [clickedRowIndex, setClickedRowIndex] = useState(null);

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = () => {
        fetch('http://localhost:3003/user/getUser')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to fetch users');
                }
                return response.json();
            })
            .then(data => setUsers(data))
            .catch(error => {
                console.error('Error fetching users:', error);
                setError('An error occurred while fetching users. Please try again later.');
            });
    };

    const handleDeleteClick = (userId) => {
        fetch(`http://localhost:3003/user/deleteUser/${userId}`, {
            method: 'DELETE'
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to delete user');
                }
                return response.json(); // Parse response JSON
            })
            .then(data => {
                console.log(data.message); // Log success message
                fetchUsers(); // Fetch updated user list after successful deletion
            })
            .catch(error => {
                console.error('Error deleting user:', error);
                setError('An error occurred while deleting the user. Please try again later.');
            });
    };

    return (
        <div className="container-box">
            {error && <div className="error">Error: {error}</div>}
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Verified</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user, index) => (
                        <tr key={user._id}>
                            <td>{user.Name}</td>
                            <td>{user.Email}</td>
                            <td>{user.Verified ? 'Yes' : 'No'}</td>
                            <td>
                                <button className='delete' onClick={() => handleDeleteClick(user._id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default UserTable;
