import './userdetails.css';
import React, { useState, useEffect } from 'react';

const UserTable = () => {
    const [users, setUsers] = useState([]);
    const [editingUser, setEditingUser] = useState(null);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = () => {
        fetch('http://localhost:3003/user/getUser')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to fetch users');
                }
                return response.json();
            })
            .then(data => setUsers(data))
            .catch(error => {
                console.error('Error fetching users:', error);
                setError('An error occurred while fetching users. Please try again later.');
            });
    };

    const handleEditClick = (user) => {
        setEditingUser({ ...user });
    };

    const handleDeleteClick = (userId) => {
        fetch(`http://localhost:3003/user/deleteUser/${userId}`, {
            method: 'DELETE'
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to delete user');
                }
                fetchUsers();
            })
            .catch(error => {
                console.error('Error deleting user:', error);
                setError('An error occurred while deleting the user. Please try again later.');
            });
    };

    const handleVerifyClick = (userId) => {
        fetch(`http://localhost:3003/user/verify/${userId}`, {
            method: 'PUT'
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to verify user');
                }
                fetchUsers();
            })
            .catch(error => {
                console.error('Error verifying user:', error);
                setError('An error occurred while verifying the user. Please try again later.');
            });
    };

    const handleEditChange = (e) => {
        const { name, value } = e.target;
        setEditingUser({ ...editingUser, [name]: value });
    };

    const handleEditSave = () => {
        fetch(`http://localhost:3003/user/update/${editingUser._UserId}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(editingUser)
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to update user');
                }
                setEditingUser(null);
                fetchUsers();
            })
            .catch(error => {
                console.error('Error updating user:', error);
                setError('An error occurred while updating the user. Please try again later.');
            });
    };

    return (
        <div className="container-box">
            {error && <div className="error">Error: {error}</div>}
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Verified</th>
                        <th>Edit</th>
                        <th>Delete</th>
                        <th>Verify</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user._UserId}>
                            <td>
                                {editingUser && editingUser._UserId === user._UserId ? (
                                    <input
                                        type="text"
                                        name="Name"
                                        value={editingUser.Name}
                                        onChange={handleEditChange}
                                    />
                                ) : (
                                    user.Name
                                )}
                            </td>
                            <td>
                                {editingUser && editingUser._UserId === user._UserId ? (
                                    <input
                                        type="email"
                                        name="Email"
                                        value={editingUser.Email}
                                        onChange={handleEditChange}
                                    />
                                ) : (
                                    user.Email
                                )}
                            </td>
                            <td>{user.Verified ? 'Yes' : 'No'}</td>
                            <td>
                                {editingUser && editingUser._UserId === user._UserId ? (
                                    <button className='save' onClick={handleEditSave}>Save</button>
                                ) : (
                                    <button className='edit' onClick={() => handleEditClick(user)}>Edit</button>
                                )}
                            </td>
                            <td>
                                <button className='delete' onClick={() => handleDeleteClick(user._UserId)}>Delete</button>
                            </td>
                            <td>
                                {!user.Verified && (
                                    <button className='verify' onClick={() => handleVerifyClick(user._UserId)}>Verify</button>
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default UserTable;
