import './userdetails.css';
import React, { useState, useEffect } from 'react';

const UserTable = () => {
    const [users, setUsers] = useState([]);
    const [error, setError] = useState('');
    const [selectedUser, setSelectedUser] = useState(null);
    const [editedName, setEditedName] = useState('');
    const [editedEmail, setEditedEmail] = useState('');
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const [clickedRowIndex, setClickedRowIndex] = useState(null);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = () => {
        fetch('http://localhost:3003/user/getUser')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to fetch users');
                }
                return response.json();
            })
            .then(data => setUsers(data))
            .catch(error => {
                console.error('Error fetching users:', error);
                setError('An error occurred while fetching users. Please try again later.');
            });
    };
    const handleDeleteClick = async (userId) => {
        setLoading(true);
        setError('');
        try {
            const response = await fetch("http://localhost:3003/user/deleteUser/${userId}", {
                method: 'DELETE'
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || 'Failed to delete user');
            }

            await fetchUsers();
            console.log('User deleted successfully');
        } catch (error) {
            console.error('Error deleting user:', error);
            setError('An error occurred while deleting the user. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    const handleVerifyClick = (userId) => {
        fetch(`http://localhost:3003/user/verifyUser/${userId}`, {
            method: 'PUT'
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to verify user');
                }
                fetchUsers();
            })
            .catch(error => {
                console.error('Error verifying user:', error);
                setError('An error occurred while verifying the user. Please try again later.');
            });
    };

    const handleEditClick = (user, index) => {
        setSelectedUser(user);
        setEditedName(user.Name);
        setEditedEmail(user.Email);
        setIsEditModalOpen(true);
        setClickedRowIndex(index);
    };

    const handleEditSave = () => {
        // Update user details and close edit modal
        const updatedUsers = [...users];
        const updatedUser = { ...selectedUser, Name: editedName, Email: editedEmail };
        updatedUsers[clickedRowIndex] = updatedUser;
        setUsers(updatedUsers);
        setIsEditModalOpen(false);
    };

    return (
        <div className="container-box">

            <>
            {isEditModalOpen && selectedUser && (
                <div className="edit-modal" style={{ top: `${(clickedRowIndex + 1) * 40}px` }}>
                    <div className="modal-content">
                        <span className="close" onClick={() => setIsEditModalOpen(false)}>&times;</span>
                        <h2>Edit User</h2>
                        <form>
                            <label>Name:</label>
                            <input type="text" value={editedName} onChange={(e) => setEditedName(e.target.value)} />
                            <label>Email:</label>
                            <input type="email" value={editedEmail} onChange={(e) => setEditedEmail(e.target.value)} />
                            <button onClick={handleEditSave}>Save</button>
                        </form>
                    </div>
                </div>
            )}

            </>
            {error && <div className="error">Error: {error}</div>}
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Verified</th>
                        <th>Delete</th>
                        <th>Verify</th>
                        <th>Edit</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user, index) => (
                        <tr key={user._UserId}>
                            <td>{user.Name}</td>
                            <td>{user.Email}</td>
                            <td>{user.Verified ? 'Yes' : 'No'}</td>
                            <td>
                                <button className='delete' onClick={() => handleDeleteClick(user._UserId)}>Delete</button>
                            </td>
                            <td>
                                {!user.Verified && (
                                    <button className='verify' onClick={() => handleVerifyClick(user._UserId)}>Verify</button>
                                )}
                            </td>
                            <td>
                                <button className='edit' onClick={() => handleEditClick(user, index)}>Edit</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
{/* 
            {isEditModalOpen && selectedUser && (
                <div className="edit-modal" style={{ top: `${(clickedRowIndex + 1) * 40}px` }}>
                    <div className="modal-content">
                        <span className="close" onClick={() => setIsEditModalOpen(false)}>&times;</span>
                        <h2>Edit User</h2>
                        <form>
                            <label>Name:</label>
                            <input type="text" value={editedName} onChange={(e) => setEditedName(e.target.value)} />
                            <label>Email:</label>
                            <input type="email" value={editedEmail} onChange={(e) => setEditedEmail(e.target.value)} />
                            <button onClick={handleEditSave}>Save</button>
                        </form>
                    </div>
                </div>
            )} */}
        </div>
    );
};

export default UserTable;
