import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Navbar from '../Components/Navbar';

function RegistrationForm() {
  const [fullname, setFullName] = useState('');
  const [username, setUserName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmpassword, setConfirmPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();

  const validate = () => {
    const newErrors = {};
    if (!fullname) newErrors.fullname = "Full Name is required";
    if (!username) newErrors.username = "User Name is required";
    if (!email) newErrors.email = "Email is required";
    else if (!/\S+@\S+\.\S+/.test(email)) newErrors.email = "Email is invalid";
    if (!password) newErrors.password = "Password is required";
    else if (password.length < 8) newErrors.password = "Password must be at least 8 characters";
    else if (!/[A-Z]/.test(password)) newErrors.password = "Password must contain at least one uppercase letter";
    else if (!/[a-z]/.test(password)) newErrors.password = "Password must contain at least one lowercase letter";
    else if (!/[0-9]/.test(password)) newErrors.password = "Password must contain at least one number";
    if (!confirmpassword) newErrors.confirmpassword = "Confirm Password is required";
    else if (password !== confirmpassword) newErrors.confirmpassword = "Passwords do not match";
    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = validate();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    setIsSubmitting(true);
    const result = { UserId: username, Name: fullname, Email: email, Password: password, Role: "admin" };

    try {
      const response = await fetch('http://localhost:3003/usercreate/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(result),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setErrors({ apiError: errorData.message });
        setIsSubmitting(false);
        return;
      }

      const data = await response.json();
      console.log('Success:', data);
      navigate('/login'); // Redirect to login page after successful registration
    } catch (error) {
      console.error('Error:', error);
      setErrors({ apiError: 'An error occurred. Please try again later.' });
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <Navbar />
      <div className="Register-center">
        <h1>Registration Form</h1>
        <form onSubmit={handleSubmit}>
          <div className="txt_field">
            <input 
              type="text" 
              required 
              value={fullname} 
              onChange={(e) => setFullName(e.target.value)} 
            />
            <span></span>
            <label>Full Name</label>
            {errors.fullname && <span className="error">{errors.fullname}</span>}
          </div>
          <div className="txt_field">
            <input 
              type="text" 
              required 
              value={username} 
              onChange={(e) => setUserName(e.target.value)} 
            />
            <span></span>
            <label>User Name</label>
            {errors.username && <span className="error">{errors.username}</span>}
          </div>
          <div className="txt_field">
            <input 
              type="email" 
              required 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
            />
            <span></span>
            <label>Email</label>
            {errors.email && <span className="error">{errors.email}</span>}
          </div>
          <div className="txt_field">
            <input 
              type="password" 
              required 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
            />
            <span></span>
            <label>Password</label>
            {errors.password && <span className="error">{errors.password}</span>}
          </div>
          <div className="txt_field">
            <input 
              type="password" 
              required 
              value={confirmpassword} 
              onChange={(e) => setConfirmPassword(e.target.value)} 
            />
            <span></span>
            <label>Confirm Password</label>
            {errors.confirmpassword && <h1 className="error">{errors.confirmpassword}</h1>}
          </div>
          <input type="submit" value="Register Now" disabled={isSubmitting} />
          {isSubmitting && <span className="loading">Registred successful</span>}
          {errors.apiError && <span className="error">{errors.apiError}</span>}
          <Link to="/login">Already have an account? Login</Link>
        </form>
      </div>
    </>
  );
}

export default RegistrationForm;
