import React from 'react';
import './Description.css';
import Navbar from '../Components/Navbar';
import Footer from '../Components/Footer';
import { useLocation, useNavigate } from 'react-router-dom';

const Description = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { title, cardImage } = location.state || {};

  const handleClick = (image) => {
    navigate("/Booking", { state: { title, image } });
  };

  return (
    <>
      <Navbar />
      <div className="body">
        <div className="heading-des">
          <h1>Image and <span>Description</span></h1>
          <p>{title}</p>
        </div>
        <div className="container-des">
          <section className="about2">
            {cardImage && cardImage.map((image, index) => (
              <div key={index} className="about2-image"> 
                <img src={image.imageUrl || image} alt={title} />
                <div className="about-content2">
                  <p>{image.Description}</p>
                  <h3>Price = {image.price}/=</h3>
                  <button className="dash1" id="dash" onClick={() => handleClick(image)}>Book Now</button>
                </div>
              </div>
            ))}
          </section>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Description;
