import React, { useState, useEffect, useRef } from "react";
import "./productadmin.css";
import emailjs from "@emailjs/browser";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

toast.configure();

function OrderList() {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState(null);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showEmailForm, setShowEmailForm] = useState(false);
  const form = useRef();

  useEffect(() => {
    // Fetch orders when the component mounts
    fetchOrders();
  }, []);

  const fetchOrders = () => {
    fetch("http://localhost:3003/order/orders")
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Failed to fetch orders: ${response.status} ${response.statusText}`);
        }
        return response.json();
      })
      .then((data) => {
        setOrders(data); // Set fetched orders to state
      })
      .catch((error) => {
        console.error("Error fetching orders:", error);
        setError("Failed to fetch orders. Please try again later.");
      });
  };

  const handleAccept = (orderId) => {
    console.log(`Accepting order with ID: ${orderId}`);
    fetch(`http://localhost:3003/order/orders/${orderId}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ status: "accepted" }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Failed to accept order: ${response.status} ${response.statusText}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Order accepted:", data);
        setOrders(
          orders.map((order) => (order._id === orderId ? data : order))
        );
        setSelectedOrder(data);
        setShowEmailForm(true);
        toast.success("Order accepted successfully!");
      })
      .catch((error) => {
        console.error("Error accepting order:", error);
        toast.error("Failed to accept the order.");
      });
  };

  const handleReject = (orderId) => {
    console.log(`Rejecting order with ID: ${orderId}`);
    fetch(`http://localhost:3003/order/orders/${orderId}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ status: "rejected" }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Failed to reject order: ${response.status} ${response.statusText}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Order rejected:", data);
        setOrders(
          orders.map((order) => (order._id === orderId ? data : order))
        );
        toast.success("Order rejected successfully!");
      })
      .catch((error) => {
        console.error("Error rejecting order:", error);
        toast.error("Failed to reject the order.");
      });
  };

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_7v4vmve",
        "template_gswksyh",
        form.current,
        "hpy_Ow2NKTCTlRRDq"
      )
      .then(
        () => {
          console.log("Email sent successfully!");
          setShowEmailForm(false);
        },
        (error) => {
          console.log("Failed to send email...", error.text);
        }
      );
  };

  return (
    <div>
      <h1 className="order">Order List</h1>
      {error && <p>{error}</p>}
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Contact Number</th>
            <th>Address</th>
            <th>Deadline</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order._id}>
              <td>{order.name}</td>
              <td>{order.email}</td>
              <td>{order.contactNumber}</td>
              <td>{order.address}</td>
              <td>{new Date(order.deadline).toLocaleDateString()}</td>
              <td>
                <button
                  className="accept"
                  onClick={() => handleAccept(order._id)}
                >
                  Accept
                </button>
                <button
                  className="reject"
                  onClick={() => handleReject(order._id)}
                >
                  Reject
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {showEmailForm && (
        <form ref={form} onSubmit={sendEmail}>
          <label>Name</label>
          <input
            type="text"
            name="user_name"
            defaultValue={selectedOrder ? selectedOrder.name : ""}
            readOnly
          />
          <label>Email</label>
          <input
            type="email"
            name="user_email"
            defaultValue={selectedOrder ? selectedOrder.email : ""}
            readOnly
          />
          <label>Message</label>
          <textarea name="message"></textarea>
          <input type="submit" value="Send" />
        </form>
      )}
    </div>
  );
}

export default OrderList;
