import React, { useState, useEffect } from 'react';
import './Paymentadmin.css';

const PaymentTable = () => {
  const [payments, setPayments] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const response = await fetch('http://localhost:3003/payment/payment');
        if (!response.ok) {
          throw new Error('Failed to fetch payments');
        }
        const paymentsData = await response.json();
        setPayments(paymentsData); // Assuming paymentsData contains an array of payments
      } catch (error) {
        console.error('Error fetching payments:', error);
        setError('An error occurred while fetching payments. Please try again later.');
      }
    };

    fetchPayments();
  }, []);

  return (
    <div className="table-container">
      <h2>Payments</h2>
      {error ? (
        <div className="error">{error}</div>
      ) : (
        <table>
          <thead>
            <tr>
              {/* <th>Payment ID</th> */}
              <th>Paid Amount</th>
              <th>Paid By</th>
              <th>Order ID</th>
              <th>Type</th>
            </tr>
          </thead>
          <tbody>
            {payments.map(payment => (
              <tr key={payment._id}>
                {/* Adjust property names based on your backend response */}
                <td>{payment.paidAmount}</td>
                <td>{payment.paidBy}</td>
                <td>{payment.orderId}</td>
                <td>{payment.paymentType}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default PaymentTable;
