import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Image, Transformation, CloudinaryContext } from 'cloudinary-react';
import Modal from '../'; // Assuming Modal.js is in the same directory
import './productadmin.css';

function Admin() {
    const [products, setProducts] = useState([]);
    const [error, setError] = useState(null);
    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [isEditConfirmationOpen, setEditConfirmationOpen] = useState(false);
    const [isDeleteConfirmationOpen, setDeleteConfirmationOpen] = useState(false);
    const [pendingEdit, setPendingEdit] = useState(null);
    const [pendingDelete, setPendingDelete] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:3003/image/getImg')
            .then(response => setProducts(response.data))
            .catch(error => {
                console.error('Error fetching products:', error);
                setError('An error occurred while fetching products. Please try again later.');
            });
    }, []);

    const handleEdit = (product) => {
        setPendingEdit(product);
        setEditConfirmationOpen(true);
    };

    const handleEditConfirm = () => {
        setSelectedProduct(pendingEdit);
        setModalIsOpen(true);
        setEditConfirmationOpen(false);
    };

    const handleEditCancel = () => {
        setEditConfirmationOpen(false);
        setPendingEdit(null);
    };

    const handleDelete = (productId) => {
        setPendingDelete(productId);
        setDeleteConfirmationOpen(true);
    };

    const handleDeleteConfirm = () => {
        axios.delete(`http://localhost:3003/image/delete/${pendingDelete}`)
            .then(() => {
                setProducts(products.filter(product => product._id !== pendingDelete));
                setDeleteConfirmationOpen(false);
                setPendingDelete(null);
            })
            .catch(error => {
                console.error('Error deleting product:', error);
                setError('An error occurred while deleting the product. Please try again later.');
                setDeleteConfirmationOpen(false);
                setPendingDelete(null);
            });
    };

    const handleDeleteCancel = () => {
        setDeleteConfirmationOpen(false);
        setPendingDelete(null);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setSelectedProduct({
            ...selectedProduct,
            [name]: value
        });
    };

    const handleFormSubmit = (e) => {
        e.preventDefault();
        axios.put(`http://localhost:3003/image/edit/${selectedProduct._id}`, selectedProduct)
            .then(response => {
                const updatedProduct = response.data;
                setProducts(products.map(product => (product._id === updatedProduct._id ? updatedProduct : product)));
                setModalIsOpen(false);
            })
            .catch(error => {
                console.error('Error updating product:', error);
                setError('An error occurred while updating the product. Please try again later.');
            });
    };

    return (
        <div>
            {error ? (
                <div className="error">Error: {error}</div>
            ) : (
                <table>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Category Name</th>
                            <th>Product Name</th>
                            <th>Description</th>
                            <th>Image</th>
                            <th>Edit</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map(product => (
                            <tr key={product._id}>
                                <td>{product._id}</td>
                                <td>{product.CategoryName}</td>
                                <td>{product.ProductName}</td>
                                <td>{product.Description}</td>
                                <td>
                                    <CloudinaryContext cloudName="your_cloud_name">
                                        <Image publicId={product.imageUrl} alt={product.ProductName} style={{ maxWidth: '50px' }}>
                                            <Transformation width="50" crop="scale" />
                                        </Image>
                                    </CloudinaryContext>
                                </td>
                                <td>
                                    <button className="edit" onClick={() => handleEdit(product)}>Edit</button>
                                </td>
                                <td>
                                    <button className="delete" onClick={() => handleDelete(product._id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}

            <Modal isOpen={modalIsOpen} onClose={() => setModalIsOpen(false)}>
                <h2>Edit Product</h2>
                {selectedProduct && (
                    <form onSubmit={handleFormSubmit}>
                        <label>
                            Category Name:
                            <input
                                type="text"
                                name="CategoryName"
                                value={selectedProduct.CategoryName}
                                onChange={handleInputChange}
                            />
                        </label>
                        <label>
                            Product Name:
                            <input
                                type="text"
                                name="ProductName"
                                value={selectedProduct.ProductName}
                                onChange={handleInputChange}
                            />
                        </label>
                        <label>
                            Description:
                            <input
                                type="text"
                                name="Description"
                                value={selectedProduct.Description}
                                onChange={handleInputChange}
                            />
                        </label>
                        <button type="submit">Save</button>
                        <button type="button" onClick={() => setModalIsOpen(false)}>Cancel</button>
                    </form>
                )}
            </Modal>

            <div className="container-box">
                {isEditConfirmationOpen && (
                    <div className="confirmation-modal">
                        <div className="modal-content">
                            <span className="close" onClick={handleEditCancel}>&times;</span>
                            <h2>Confirm Edit</h2>
                            <p>Are you sure you want to edit this product?</p>
                            <button onClick={handleEditConfirm}>Yes</button>
                            <button onClick={handleEditCancel}>No</button>
                        </div>
                    </div>
                )}
                {isDeleteConfirmationOpen && (
                    <div className="confirmation-modal">
                        <div className="modal-content">
                            <span className="close" onClick={handleDeleteCancel}>&times;</span>
                            <h2>Confirm Delete</h2>
                            <p>Are you sure you want to delete this product?</p>
                            <button onClick={handleDeleteConfirm}>Yes</button>
                            <button onClick={handleDeleteCancel}>No</button>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}

export default Admin;
