import React, { useState, useEffect } from 'react';
import axios from 'axios';

function OrderList() {
    const [orders, setOrders] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Fetch orders when component mounts
        axios.get('/api/orders') // Replace with your actual backend endpoint
            .then(response => {
                setOrders(response.data); // Set fetched orders to state
            })
            .catch(error => {
                console.error('Error fetching orders:', error);
                setError('Failed to fetch orders. Please try again later.');
            });
    }, []);

    const handleAccept = (orderId) => {
        // Example of handling accept button click
        // You can implement your logic here to update the order status, e.g., send a PATCH request
        console.log('Accepted order:', orderId);
    };

    const handleReject = (orderId) => {
        // Example of handling reject button click
        // You can implement your logic here to update the order status, e.g., send a PATCH request
        console.log('Rejected order:', orderId);
    };

    return (
        <div>
            <h1>Order List</h1>
            {error && <p>{error}</p>}
            <ul>
                {orders.map(order => (
                    <li key={order._id}>
                        <h2>{order.Name}</h2>
                        <p>Contact Number: {order.ContactNumber}</p>
                        <p>Address: {order.Address}</p>
                        <p>Deadline: {order.DeadLine}</p> 
                        <button onClick={() => handleAccept(order._id)}>Accept</button>
                        <button onClick={() => handleReject(order._id)}>Reject</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default OrderList;
