import './userdetails.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserTable = () => {
    const [users, setUsers] = useState([]);
    const [editingUserId, setEditingUserId] = useState(null);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = () => {
        axios.get('http://localhost:3003/user/getUser')
            .then(response => setUsers(response.data))
            .catch(error => {
                console.error('Error fetching users:', error);
                setError('An error occurred while fetching users. Please try again later.');
            });
    };

    const handleEditClick = (userId) => {
        setEditingUserId(userId);
    };

    const handleCancelClick = () => {
        setEditingUserId(null);
    };

    const handleDeleteClick = (userId) => {
        axios.delete(`http://localhost:3003/user/deleteUser/${userId}`)
            .then(response => {
                fetchUsers();
            })
            .catch(error => {
                console.error('Error deleting user:', error);
                setError('An error occurred while deleting the user. Please try again later.');
            });
    };

    const handleVerifyClick = (userId) => {
        axios.put(`http://localhost:3003/user/verify/${userId}`)
            .then(response => {
                fetchUsers();
            })
            .catch(error => {
                console.error('Error verifying user:', error);
                setError('An error occurred while verifying the user. Please try again later.');
            });
    };

    const handleEditChange = (e, userId) => {
        const { name, value } = e.target;
        setUsers(users.map(user =>
            user._UserId === userId ? { ...user, [name]: value } : user
        ));
    };

    const handleEditSave = (userId) => {
        const editedUser = users.find(user => user._UserId === userId);
        axios.put(`http://localhost:3003/user/update/${userId}`, editedUser)
            .then(response => {
                setEditingUserId(null);
                fetchUsers();
            })
            .catch(error => {
                console.error('Error updating user:', error);
                setError('An error occurred while updating the user. Please try again later.');
            });
    };

    return (
        <div className="container-box">
            {error && <div className="error">Error: {error}</div>}
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Verified</th>
                        <th>Edit</th>
                        <th>Delete</th>
                        <th>Verify</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user._UserId}>
                            <td>
                                {editingUserId === user._UserId ? (
                                    <input
                                        type="text"
                                        name="Name"
                                        value={user.Name}
                                        onChange={(e) => handleEditChange(e, user._UserId)}
                                    />
                                ) : (
                                    user.Name
                                )}
                            </td>
                            <td>
                                {editingUserId === user._UserId ? (
                                    <input
                                        type="email"
                                        name="Email"
                                        value={user.Email}
                                        onChange={(e) => handleEditChange(e, user._UserId)}
                                    />
                                ) : (
                                    user.Email
                                )}
                            </td>
                            <td>{user.Verified ? 'Yes' : 'No'}</td>
                            <td>
                                {editingUserId === user._UserId ? (
                                    <div>
                                        <button className='save' onClick={() => handleEditSave(user._UserId)}>Save</button>
                                        <button className='cancel' onClick={handleCancelClick}>Cancel</button>
                                    </div>
                                ) : (
                                    <button className='edit' onClick={() => handleEditClick(user._UserId)}>Edit</button>
                                )}
                            </td>
                            <td>
                                <button className='delete' onClick={() => handleDeleteClick(user._UserId)}>Delete</button>
                            </td>
                            <td>
                                {!user.Verified && (
                                    <button className='verify' onClick={() => handleVerifyClick(user._UserId)}>Verify</button>
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default UserTable;
