import React, { useState } from 'react';
import './Register.css';
import Navbar from '../Components/Navbar';
import { Link, useNavigate } from 'react-router-dom';

function RegistrationForm() {
  const [fullname, setFullName] = useState('');
  const [username, setUserName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmpassword, setConfirmPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showSuccessMessage, setShowSuccessMessage] = useState(false); // State for showing success message
  const navigate = useNavigate();

  const validate = () => {
    const newErrors = {};
    // Your validation logic
    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = validate();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    setIsSubmitting(true);
    const result = { UserId: username, Name: fullname, Email: email, Password: password, Role: "user" };

    try {
      const response = await fetch('http://localhost:3003/usercreate/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(result),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setErrors({ apiError: errorData.message });
        setIsSubmitting(false);
        return;
      }

      // Registration successful
      setShowSuccessMessage(true); // Show success message
      setTimeout(() => {
        navigate('/login'); // Redirect to login page after successful registration
      }, 2000); // Redirect after 2 seconds

    } catch (error) {
      console.error('Error:', error);
      setErrors({ apiError: 'An error occurred. Please try again later.' });
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <Navbar />
      <div className="Register-center">
        <h1>Registration Form</h1>
        <form onSubmit={handleSubmit}>
          {/* Your form fields */}
          <input type="submit" value="Register Now" disabled={isSubmitting} />
          {isSubmitting && <span className="loading">Submitting...</span>}
          {errors.apiError && <span className="error">{errors.apiError}</span>}
          {showSuccessMessage && <span className="success">Registered successfully!</span>} {/* Show success message */}
          <Link to="/Popup"></Link>
        </form>
      </div>
    </>
  );
}

export default RegistrationForm;
