// Emproidry.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Navbar from '../Components/Navbar';
import Footer from '../Components/Footer';

const Emproidry = () => {
  const { title } = useParams();
  const [cardImage, setCardImage] = useState([]);

  useEffect(() => {
    const fetchImages = async () => {
      try {
        const response = await axios.get(`http://localhost:3003/image/showimage/${title}`);
        setCardImage(response.data);
      } catch (error) {
        console.error('Error fetching card images:', error);
      }
    };

    fetchImages();
  }, [title]);

  return (
    <>
      <Navbar />
      <div className="emp-head">{title}<span> Designs</span></div>
      <div className="container-emp">
        {cardImage.map((image, index) => (
          <div key={index} className="card-emp">
            <img src={image.imageUrl} alt={`Image ${index}`} />
            <p>{image.description}</p>
            <a href="Description" className="button">View </a>
          </div>
        ))}
      </div>
      <Footer />
    </>
  );
};

export default Emproidry;
