import React, { useState, useEffect } from "react";
import axios from "axios";
import Modal from "../Components/Modal"; // Assuming Modal.js is correctly implemented and imported
import "./productadmin.css";
import AdminDashboard from "./Dashboard";

function OrderList() {
	const [orders, setOrders] = useState([]);
	const [error, setError] = useState(null);
	const [modalIsOpen, setModalIsOpen] = useState(false);
	const [selectedOrder, setSelectedOrder] = useState(null);

	useEffect(() => {
		// Fetch orders when component mounts
		axios
			.get("http://localhost:3003/order/orders")
			.then((response) => {
				setOrders(response.data); // Set fetched orders to state
			})
			.catch((error) => {
				console.error("Error fetching orders:", error);
				setError("Failed to fetch orders. Please try again later.");
			});
	}, []);

	const handleAccept = (orderId) => {
		axios
			.patch(`http://localhost:3003/order/orders/${orderId}`, {
				status: "accepted",
			})
			.then((response) => {
				console.log("Order accepted:", response.data);
				setOrders(
					orders.map((order) => (order._id === orderId ? response.data : order))
				);
				sendNotification(response.data);
			})
			.catch((error) => {
				console.error("Error accepting order:", error);
			});
	};

	const handleReject = (orderId) => {
		axios
			.patch(`http://localhost:3003/order/orders/${orderId}`, {
				status: "rejected",
			})
			.then((response) => {
				console.log("Order rejected:", response.data);
				setOrders(
					orders.map((order) => (order._id === orderId ? response.data : order))
				);
				sendNotification(response.data);
			})
			.catch((error) => {
				console.error("Error rejecting order:", error);
			});
	};

	const sendNotification = (order) => {
		axios
			.post("http://localhost:3003/order/notify", order)
			.then((response) => {
				console.log("Notification sent:", response.data);
			})
			.catch((error) => {
				console.error("Error sending notification:", error);
			});
	};

	const openModal = (order) => {
		setSelectedOrder(order);
		setModalIsOpen(true);
	};

	return (
		<div>
			<AdminDashboard />
			<h1>Order List</h1>
			{error && <p>{error}</p>}
			<table>
				<thead>
					<tr>
						<th>Name</th>
						<th>Gmail</th>
						<th>Contact Number</th>
						<th>Address</th>
						<th>Deadline</th>
						<th>Actions</th>
					</tr>
				</thead>
				<tbody>
					{orders.map((order) => (
						<tr key={order._id}>
							<td>{order.name}</td>
							<td>{order.email}</td>
							<td>{order.contactNumber}</td>
							<td>{order.address}</td>
							<td>{new Date(order.deadline).toLocaleDateString()}</td>
							<td>
								<button className="accept" onClick={() => openModal(order)}>
									Accept
								</button>
								<button className="reject" onClick={() => openModal(order)}>
									Reject
								</button>
							</td>
						</tr>
					))}
				</tbody>
			</table>

			{modalIsOpen && (
				<Modal isOpen={modalIsOpen} onClose={() => setModalIsOpen(false)}>
					<h2>Order Confirmation</h2>
					{selectedOrder && (
						<div>
							<p>Order ID: {selectedOrder._id}</p>
							<p>Name: {selectedOrder.name}</p>
							<p>Contact Number: {selectedOrder.contactNumber}</p>
							<p>Address: {selectedOrder.address}</p>
							<p>Deadline: {selectedOrder.deadline}</p>
							<p>Are you sure you want to accept/reject this order?</p>
							<button
								onClick={() => {
									handleAccept(selectedOrder._id);
									setModalIsOpen(false);
								}}
							>
								Accept
							</button>
							<button
								onClick={() => {
									handleReject(selectedOrder._id);
									setModalIsOpen(false);
								}}
							>
								Reject
							</button>
						</div>
					)}
				</Modal>
			)}
		</div>
	);
}

export default OrderList;
