import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import "./productadmin.css";
import emailjs from '@emailjs/browser';

function OrderList() {
    const [orders, setOrders] = useState([]);
    const [error, setError] = useState(null);
    const form = useRef();

    useEffect(() => {
        // Fetch orders when the component mounts
        axios
            .get("http://localhost:3003/order")
            .then((response) => {
                setOrders(response.data); // Set fetched orders to state
            })
            .catch((error) => {
                console.error("Error fetching orders:", error);
                setError("Failed to fetch orders. Please try again later.");
            });
    }, []);

    const handleUpdateOrder = (orderId, status, successCallback) => {
        axios
            .patch(`http://localhost:3003/order/${orderId}`, { status })
            .then((response) => {
                setOrders(
                    orders.map((order) => (order._id === orderId ? response.data : order))
                );
                successCallback(response.data); // Call the provided callback function
            })
            .catch((error) => {
                console.error(`Error updating order to ${status}:`, error);
            });
    };

    const handleAccept = (orderId) => {
        handleUpdateOrder(orderId, "accepted", sendAcceptEmail);
    };

    const handleReject = (orderId) => {
        handleUpdateOrder(orderId, "rejected", sendRejectEmail);
    };

    const sendEmail = (templateParams) => {
        emailjs
            .send('service_7v4vmve', 'template_gswksyh', templateParams, 'hpy_Ow2NKTCTlRRDq')
            .then(
                () => {
                    console.log('Email sent successfully!');
                },
                (error) => {
                    console.log('Failed to send email...', error.text);
                }
            );
    };

    const sendAcceptEmail = (order) => {
        const templateParams = {
            user_name: order.name,
            user_email: order.email,
            message: `Dear ${order.name},\n\nYour order has been accepted.\n\nThank you.`
        };
        sendEmail(templateParams);
    };

    const sendRejectEmail = (order) => {
        const templateParams = {
            user_name: order.name,
            user_email: order.email,
            message: `Dear ${order.name},\n\nWe regret to inform you that your order has been rejected.\n\nThank you.`
        };
        sendEmail(templateParams);
    };

    return (
        <div>
            <h1 className="order">Order List</h1>
            {error && <p>{error}</p>}
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Contact Number</th>
                        <th>Address</th>
                        <th>Deadline</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map((order) => (
                        <tr key={order._id}>
                            <td>{order.name}</td>
                            <td>{order.email}</td>
                            <td>{order.contactNumber}</td>
                            <td>{order.address}</td>
                            <td>{new Date(order.deadline).toLocaleDateString()}</td>
                            <td>
                                <button className="accept" onClick={() => handleAccept(order._id)}>
                                    Accept
                                </button>
                                <button className="reject" onClick={() => handleReject(order._id)}>
                                    Reject
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default OrderList;
