import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from '../Components/Modal'; // Assuming Modal.js is correctly implemented and imported
import './productadmin.css';

function OrderList() {
    const [orders, setOrders] = useState([]);
    const [error, setError] = useState(null);
    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [selectedOrder, setSelectedOrder] = useState(null);

    useEffect(() => {
        // Fetch orders when component mounts
        axios.get('http://localhost:3003/order/orders')
            .then(response => {
                setOrders(response.data); // Set fetched orders to state
            })
            .catch(error => {
                console.error('Error fetching orders:', error);
                setError('Failed to fetch orders. Please try again later.');
            });
    }, []);

    const handleAccept = (orderId) => {
        axios.patch(`http://localhost:3003/order/orders/${orderId}`, { status: 'accepted' })
            .then(response => {
                console.log('Order accepted:', response.data);
                setOrders(orders.map(order => order._id === orderId ? response.data : order));
                sendNotification(response.data);
            })
            .catch(error => {
                console.error('Error accepting order:', error);
            });
    };

    const handleReject = (orderId) => {
        axios.patch(`http://localhost:3003/order/orders/${orderId}`, { status: 'rejected' })
            .then(response => {
                console.log('Order rejected:', response.data);
                setOrders(orders.map(order => order._id === orderId ? response.data : order));
                sendNotification(response.data);
            })
            .catch(error => {
                console.error('Error rejecting order:', error);
            });
    };

    const sendNotification = (order) => {
        axios.post('http://localhost:3003/order/notify', order)
            .then(response => {
                console.log('Notification sent:', response.data);
            })
            .catch(error => {
                console.error('Error sending notification:', error);
            });
    };

    const openModal = (order) => {
        setSelectedOrder(order);
        setModalIsOpen(true);
    };

    return (
        <div>
            <h1>Order List</h1>
            {error && <p>{error}</p>}
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Contact Number</th>
                        <th>Address</th>
                        <th>Deadline</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map(order => (
                        <tr key={order._id}>
                            <td>{order.name}</td>
                            <td>{order.contactNumber}</td>
                            <td>{order.address}</td>
                            <td>{new Date(order.deadline).toLocaleDateString()}</td>
                            <td>
                                <button className="accept" onClick={() => openModal(order)}>Accept</button>
                                <button className="reject" onClick={() => openModal(order)}>Reject</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>


            )}
        </div>
    );
}

export default OrderList;
