import "./userdetails.css";
import "./Admin.css";
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";
import customer02 from "../Assets/customer02.jpeg";
import Modal from "../Components/Modal";
import ProductForm from "./Addproduct";

const UserTable = () => {
	const [search, setSearch] = useState("");
	const [showModal, setShowModal] = useState(false);
	const [usersCount, setUsersCount] = useState(0);
	const [ordersCount, setOrdersCount] = useState(0);
	const [productsCount, setProductsCount] = useState(0);
	const [currentPage, setCurrentPage] = useState(1);
	const usersPerPage = 5;
	const [users, setUsers] = useState([]);
	const [error, setError] = useState("");
	const [selectedUser, setSelectedUser] = useState(null);
	const [editedName, setEditedName] = useState("");
	const [editedEmail, setEditedEmail] = useState("");
	const [isEditModalOpen, setIsEditModalOpen] = useState(false);
	const [isEditConfirmationOpen, setIsEditConfirmationOpen] = useState(false);
	const [isDeleteConfirmationOpen, setIsDeleteConfirmationOpen] =
		useState(false);

	useEffect(() => {
		fetchUsersCount();
		fetchOrdersCount();
		fetchProductsCount();
		fetchUsers();
	}, [currentPage]);

	const fetchUsersCount = async () => {
		try {
			const response = await fetch("http://localhost:3003/user/count");
			if (!response.ok) {
				throw new Error("Network response was not ok");
			}
			const data = await response.json();
			setUsersCount(data.count);
		} catch (error) {
			console.error("Error fetching users count:", error);
		}
	};

	const fetchOrdersCount = async () => {
		try {
			const response = await fetch("http://localhost:3003/orders/count");
			if (!response.ok) {
				throw new Error("Network response was not ok");
			}
			const data = await response.json();
			setOrdersCount(data.count);
		} catch (error) {
			console.error("Error fetching orders count:", error);
		}
	};

	const fetchProductsCount = async () => {
		try {
			const response = await fetch("http://localhost:3003/products/count");
			if (!response.ok) {
				throw new Error("Network response was not ok");
			}
			const data = await response.json();
			setProductsCount(data.count);
		} catch (error) {
			console.error("Error fetching products count:", error);
		}
	};

	const fetchUsers = () => {
		fetch(
			`http://localhost:3003/user/getUser?page=${currentPage}&limit=${usersPerPage}`
		)
			.then((response) => {
				if (!response.ok) {
					throw new Error("Failed to fetch users");
				}
				return response.json();
			})
			.then((data) => setUsers(data))
			.catch((error) => {
				console.error("Error fetching users:", error);
				setError(
					"An error occurred while fetching users. Please try again later."
				);
			});
	};

	const handleSearchChange = (e) => {
		setSearch(e.target.value);
	};

	const handleLogout = () => {
		localStorage.removeItem("token");
		toast.success("Logout successfully!");
	};

	const handleShow = () => setShowModal(true);
	const handleClose = () => setShowModal(false);

	const handleEditClick = (user) => {
		setSelectedUser(user);
		setIsEditConfirmationOpen(true);
	};

	const handleDeleteClick = (user) => {
		setSelectedUser(user);
		setIsDeleteConfirmationOpen(true);
	};

	const handleEditConfirm = () => {
		setEditedName(selectedUser.Name);
		setEditedEmail(selectedUser.Email);
		setIsEditModalOpen(true);
		setIsEditConfirmationOpen(false);
	};

	const handleDeleteConfirm = () => {
		deleteHandler(selectedUser._id);
		setIsDeleteConfirmationOpen(false);
	};

	const handleEditCancel = () => {
		setSelectedUser(null);
		setIsEditConfirmationOpen(false);
	};

	const handleDeleteCancel = () => {
		setSelectedUser(null);
		setIsDeleteConfirmationOpen(false);
	};

	const handleEditSave = (e) => {
		e.preventDefault();
		fetch(`http://localhost:3003/user/updateUser/${selectedUser._id}`, {
			method: "PUT",
			headers: {
				"Content-Type": "application/json",
			},
			body: JSON.stringify({ Name: editedName, Email: editedEmail }),
		})
			.then((response) => {
				if (!response.ok) {
					throw new Error("Failed to update user");
				}
				setIsEditModalOpen(false);
				fetchUsers();
			})
			.catch((error) => {
				console.error("Error updating user:", error);
				setError(
					"An error occurred while updating the user. Please try again later."
				);
			});
	};

	const deleteHandler = (userId) => {
		fetch(`http://localhost:3003/user/deleteUser/${userId}`, {
			method: "DELETE",
		})
			.then((response) => {
				if (!response.ok) {
					throw new Error("Failed to delete user");
				}
				fetchUsers();
			})
			.catch((error) => {
				console.error("Error deleting user:", error);
				setError(
					"An error occurred while deleting the user. Please try again later."
				);
			});
	};

	const handlePageChange = (pageNumber) => {
		setCurrentPage(pageNumber);
	};

	const totalPages = Math.ceil(usersCount / usersPerPage);

	return (
		<div>
			<div className="dashboard">
				<div className="sidebar">
					<div className="sidebar-header">
						<h2>Rare Fairy</h2>
					</div>
					<ul className="sidebar-menu">
						<li>
							<Link to="/Dashboard">
								<i className="fas fa-tachometer-alt"></i> Dashboard
							</Link>
						</li>
						<li>
							<Link to="/OrderList">
								<i className="fas fa-shopping-cart"></i> Order
							</Link>
						</li>
						<li>
							<Link to="/Admin">
								<i className="fas fa-users"></i> Users
							</Link>
						</li>
						<li>
							<a href="/product">
								<i className="fas fa-box-open"></i> Product{" "}
							</a>
						</li>
						<li>
							<Link to="/payment">
								<i className="fas fa-credit-card"></i> Payment
							</Link>
						</li>
						<li>
							<Link className="a" to="/login" onClick={handleLogout}>
								<i className="fas fa-sign-out-alt"></i> Logout
							</Link>
						</li>
					</ul>
				</div>
				<div className="main-content">
					<div className="header">
						<div className="header-left">
							<h1>Welcome to Admin</h1>
						</div>
						<div className="header-right">
							<div className="search-bar">
								<input
									type="text"
									placeholder="Search..."
									value={search}
									onChange={handleSearchChange}
								/>
							</div>
							<div className="notification">
								<i className="fas fa-bell"></i>
							</div>
							<div className="profile">
								<img src={customer02} alt="Profile" />
								<span>Manojini Suthakar</span>
							</div>
						</div>
					</div>
					<div className="content">
						<div className="card-dash">
							<h3>Users</h3>
							<p>Number of active users: {usersCount}</p>
						</div>
						<div className="card-dash">
							<h3>Orders</h3>
							<p>Number of orders: {ordersCount}</p>
						</div>
						<div className="card-dash">
							<Link to="/Addproduct" onClick={handleShow}>
								<h3>Add product</h3>
								<p>Number of products: {productsCount}</p>
							</Link>
						</div>
					</div>
				</div>
			</div>
			<Modal show={showModal} handleClose={handleClose}>
				<ProductForm />
			</Modal>
			<div className="container-box">
				<h1>Users</h1>
				<table className="user-table">
					<thead>
						<tr>
							<th>Name</th>
							<th>Email</th>
							<th>Action</th>
						</tr>
					</thead>
					<tbody>
						{users.map((user) => (
							<tr key={user._id}>
								<td>{user.Name}</td>
								<td>{user.Email}</td>
								<td>
									<button onClick={() => handleEditClick(user)}>Edit</button>
									<button onClick={() => handleDeleteClick(user)}>
										Delete
									</button>
								</td>
							</tr>
						))}
					</tbody>
				</table>
				<div className="pagination">
					<button
						onClick={() => handlePageChange(currentPage - 1)}
						disabled={currentPage === 1}
					>
						Previous
					</button>
					<span>
						{currentPage} of {totalPages}
					</span>
					<button
						onClick={() => handlePageChange(currentPage + 1)}
						disabled={currentPage === totalPages}
					>
						Next
					</button>
				</div>
			</div>
			{isEditConfirmationOpen && (
				<div className="confirmation-modal">
					<div className="modal-content">
						<span className="close" onClick={handleEditCancel}>
							&times;
						</span>
						<h2>Confirm Edit</h2>
						<p>Are you sure you want to edit this user?</p>
						<button onClick={handleEditConfirm}>Yes</button>
						<button onClick={handleEditCancel}>No</button>
					</div>
				</div>
			)}
			{isDeleteConfirmationOpen && (
				<div className="confirmation-modal">
					<div className="modal-content">
						<span className="close" onClick={handleDeleteCancel}>
							&times;
						</span>
						<h2>Confirm Delete</h2>
						<p>Are you sure you want to delete this user?</p>
						<button onClick={handleDeleteConfirm}>Yes</button>
						<button onClick={handleDeleteCancel}>No</button>
					</div>
				</div>
			)}
			{isEditModalOpen && selectedUser && (
				<div className="edit-modal">
					<div className="modal-content">
						<span className="close" onClick={() => setIsEditModalOpen(false)}>
							&times;
						</span>
						<h2>Edit User</h2>
						<form onSubmit={handleEditSave}>
							<label>Name:</label>
							<input
								type="text"
								value={editedName}
								onChange={(e) => setEditedName(e.target.value)}
								required
							/>
							<label>Email:</label>
							<input
								type="email"
								value={editedEmail}
								onChange={(e) => setEditedEmail(e.target.value)}
								required
							/>
							<button type="submit">Save</button>
						</form>
					</div>
				</div>
			)}
			{error && <div className="error">Error: {error}</div>}
		</div>
	);
};

export default UserTable;
