import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './productadmin.css';

function Admin() {
    const [products, setProducts] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:3003/image/getImg')
            .then(response => setProducts(response.data))
            .catch(error => {
                console.error('Error fetching products:', error);
                setError('An error occurred while fetching products. Please try again later.');
            });
    }, []);

    const handleEdit = (productId) => {
        console.log('Edit product with ID:', productId);
        // You can navigate to the edit page or modal here
    };

    const handleDelete = (productId) => {
        console.log('Delete product with ID:', productId);
        axios.delete(`http://localhost:3003/image/delete/${productId}`)
            .then(() => {
                setProducts(products.filter(product => product._id !== productId));
            })
            .catch(error => {
                console.error('Error deleting product:', error);
                setError('An error occurred while deleting product. Please try again later.');
            });
    };

    return (
        <div>
            {error ? (
                <div className="error">Error: {error}</div>
            ) : (
                <table>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Category Name</th>
                            <th>Product Name</th>
                            <th>Description</th>
                            <th>Image</th>
                            <th>Edit</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map(product => (
                            <tr key={product._id}>
                                <td>{product._id}</td>
                                <td>{product.CategoryName}</td>
                                <td>{product.ProductName}</td>
                                <td>{product.Description}</td>
                                <td><img src={`http://localhost:3003/${product.ImageId}`} alt={product.ProductName} style={{ maxWidth: '100px' }}/></td>
                                <td>
                                    <button className="edit" onClick={() => handleEdit(product._id)}>Edit</button>
                                </td>
                                <td>
                                    <button className="delete" onClick={() => handleDelete(product._id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default Admin;
