import './userdetails.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

const UserTable = () => {
    const [users, setUsers] = useState([]);
    const [error, setError] = useState('');
    const navigate = useNavigate(); // Initialize navigate function

    useEffect(() => {
        axios.get('http://localhost:3003/user/getuser')
            .then(response => setUsers(response.data))
            log
            .catch(error => {
                console.error('Error fetching users:', error);
                setError('An error occurred while fetching users. Please try again later.');
            });
    }, []);

    const handleEdit = (userId) => {
        navigate(`/edit/${userId}`); // Redirect to the edit page with the user ID
    };

    const handleDelete = (userId) => {
        axios.delete(`http://localhost:3003/user/delete/${userId}`)
            .then(response => {
                console.log('User deleted:', response);
                setUsers(users.filter(user => user._id !== userId)); // Update the user list
            })
            .catch(error => {
                console.error('Error deleting user:', error);
                setError('An error occurred while deleting the user. Please try again later.');
            });
    };

    const handleVerify = (userId) => {
        axios.put(`http://localhost:3003/user/verify/${userId}`)
            .then(response => {
                console.log('User verified:', response);
                setUsers(users.map(user => 
                    user._id === userId ? { ...user, verified: true } : user
                )); // Update the user verification status
            })
            .catch(error => {
                console.error('Error verifying user:', error);
                setError('An error occurred while verifying the user. Please try again later.');
            });
    };

    return (
        <>
            <div className="container-box">
                {error ? (
                    <div className="error">Error: {error}</div>
                ) : (
                    <table>
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Edit</th>
                                <th>Delete</th>
                                <th>Verify</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.map(user => (
                                <tr key={user._id}>
                                    <td>{user.name}</td>
                                    <td>{user.email}</td>
                                    <td>
                                        <button 
                                            className='edit' 
                                            type='button' 
                                            onClick={() => handleEdit(user._id)}
                                        >
                                            Edit
                                        </button>
                                        <button 
                                            className='delete' 
                                            type='button' 
                                            onClick={() => handleDelete(user._id)}
                                        >
                                            Delete
                                        </button>
                                        <button 
                                            className='verify' 
                                            type='button' 
                                            onClick={() => handleVerify(user._id)}
                                            disabled={user.verified} // Disable if already verified
                                        >
                                            {user.verified ? 'Verified' : 'Verify'}
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </>
    );
};

export default UserTable;
