import React, { useState, useEffect } from 'react';
import './Addproduct.css';

function ProductForm() {
  const [productName, setProductName] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [categoryName, setCategoryName] = useState('');
  const [photo, setPhoto] = useState(null);
  const [errors, setErrors] = useState({});
  const [products, setProducts] = useState([]);

  const validate = () => {
    const newErrors = {};
    if (!productName) newErrors.productName = "Product Name is required";
    if (!description) newErrors.description = "Description is required";
    if (!price) newErrors.price = "Price is required";
    else if (isNaN(price) || price <= 0) newErrors.price = "Price must be a positive number";
    if (!categoryName) newErrors.categoryName = "Category Name is required";
    if (!photo) newErrors.photo = "Photo is required";
    return newErrors;
  };

  const fetchProducts = async () => {
    try {
      const response = await fetch('http://localhost:3003/products', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        }
      });
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      console.log('Fetched products:', data); // Log fetched products
      setProducts(data);
    } catch (error) {
      console.error('Error fetching products:', error); // Log detailed error
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = validate();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    const formData = new FormData();
    formData.append('name', productName);
    formData.append('description', description);
    formData.append('price', parseFloat(price));
    formData.append('category', categoryName);
    formData.append('photo', photo);

    try {
      const response = await fetch('http://localhost:3003/image/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      console.log('Success:', data);

      // Clear the form
      setProductName('');
      setDescription('');
      setPrice('');
      setCategoryName('');
      setPhoto(null);
      setErrors({});

      // Fetch the updated list of products
      fetchProducts();
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="ProductForm">
      <h1>Add Product</h1>
      <form onSubmit={handleSubmit}>
        <div className="txt_field">
          <input 
            type="text" 
            required 
            value={productName} 
            onChange={(e) => setProductName(e.target.value)} 
          />
          <span></span>
          <label>Product Name</label>
          {errors.productName && <span className="error">{errors.productName}</span>}
        </div>
        <div className="txt_field">
          <input 
            type="text" 
            required 
            value={description} 
            onChange={(e) => setDescription(e.target.value)} 
          />
          <span></span>
          <label>Description</label>
          {errors.description && <span className="error">{errors.description}</span>}
        </div>
        <div className="txt_field">
          <input 
            type="text" 
            required 
            value={price} 
            onChange={(e) => setPrice(e.target.value)} 
          />
          <span></span>
          <label>Price</label>
          {errors.price && <span className="error">{errors.price}</span>}
        </div>
        <div className="txt_field">
          <input 
            type="text" 
            required 
            value={categoryName} 
            onChange={(e) => setCategoryName(e.target.value)} 
          />
          <span></span>
          <label>Category Name</label>
          {errors.categoryName && <span className="error">{errors.categoryName}</span>}
        </div>
        <div className="txt_field">
          <input 
            type="file" 
            required 
            onChange={(e) => setPhoto(e.target.files[0])} 
          />
          <span></span>
          <label>Photo</label>
          {errors.photo && <span className="error">{errors.photo}</span>}
        </div>
        <input className='ap' type="submit" value="Add Product" />
      </form>

      <h2>Product List</h2>
      <ul>
        {products.map(product => (
          <li key={product.id}>
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>Price: {product.price}</p>
            <p>Category: {product.category}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProductForm;
