import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './Addproduct.css';

function ProductForm() {
  const [productName, setProductName] = useState('');
  const [description, setDescription] = useState('');
  const [categoryName, setCategoryName] = useState('');
  const [photo, setPhoto] = useState(null);
  const [errors, setErrors] = useState({});

  const validate = () => {
    const newErrors = {};
    if (!productName) newErrors.productName = "Product Name is required";
    if (!description) newErrors.description = "Description is required";
    if (!categoryName) newErrors.categoryName = "Category Name is required";
    if (!photo) newErrors.photo = "Photo is required";
    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = validate();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    const formData = new FormData();
    formData.append('ProductName', productName);
    formData.append('Description', description);
    formData.append('CategoryName', categoryName);
    formData.append('photo', photo);

    try {
      const response = await fetch('http://localhost:3003/image/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorText = await response.text(); // Get error text for debugging
        throw new Error(`HTTP error! Status: ${response.status}, Message: ${errorText}`);
      }

      const data = await response.json();
      toast.success('Product added successfully!');

      // Clear the form
      setProductName('');
      setDescription('');
      setCategoryName('');
      setPhoto(null);
      setErrors({});

      // Fetch the updated list of products if necessary
      // fetchProducts(); // Uncomment if defined elsewhere
    } catch (error) {
      console.error('Error:', error);
      toast.error('Failed to add product. Please try again.');
    }
  };

  return (
    <div className="container">
      <h2>Add New Product</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            className="form-control"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          {errors.name && <span className="error text-danger">{errors.name}</span>}
        </div>
        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            className="form-control"
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          ></textarea>
          {errors.description && <span className="error text-danger">{errors.description}</span>}
        </div>
        {/* <div className="form-group">
          <label htmlFor="price">Price</label>
          <input
            type="number"
            className="form-control"
            id="price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            required
          />
          {errors.price && <span className="error text-danger">{errors.price}</span>}
        </div> */}
        <div className="form-group">
          <label htmlFor="stock">Stock</label>
          <input
            type="number"
            className="form-control"
            id="stock"
            value={stock}
            onChange={(e) => setStock(e.target.value)}
            required
          />
          {errors.stock && <span className="error text-danger">{errors.stock}</span>}
        </div>
        <div className="form-group">
          <label htmlFor="categoryName">Category Name</label>
          <select
            className="form-control"
            id="categoryName"
            value={categoryName}
            onChange={(e) => setCategoryName(e.target.value)}
            required
          >
            <option value="">Select a category</option>
            {categories.map((category) => (
              <option key={category.id} value={category.name}>
                {category.name}
              </option>
            ))}
          </select>
          {errors.categoryName && <span className="error text-danger">{errors.categoryName}</span>}
        </div>
        <button type="submit" className="btn btn-primary">Add Product</button>
      </form>
    </div>
  );
}

export default ProductForm;
