import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Image, Transformation, CloudinaryContext } from 'cloudinary-react';
import './productadmin.css';

function Admin() {
    const [products, setProducts] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:3003/image/getImg')
            .then(response => setProducts(response.data))
            .catch(error => {
                console.error('Error fetching products:', error);
                setError('An error occurred while fetching products. Please try again later.');
            });
    }, []);

    const handleEdit = (productId) => {
        // Implement your logic to edit a product, e.g., open a modal with a form to edit the product details.
        console.log('Edit product with ID:', productId);
    };

    const handleDelete = (productId) => {
        axios.delete(`http://localhost:3003/image/delete/${productId}`)
            .then(() => {
                setProducts(products.filter(product => product._id !== productId));
            })
            .catch(error => {
                console.error('Error deleting product:', error);
                setError('An error occurred while deleting the product. Please try again later.');
            });
    };

    return (
        <div>
            {error ? (
                <div className="error">Error: {error}</div>
            ) : (
                <table>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Category Name</th>
                            <th>Product Name</th>
                            <th>Description</th>
                            <th>Image</th>
                            <th>Edit</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map(product => (
                            <tr key={product._id}>
                                <td>{product._id}</td>
                                <td>{product.CategoryName}</td>
                                <td>{product.ProductName}</td>
                                <td>{product.Description}</td>
                                <td>
                                    <CloudinaryContext cloudName="your_cloud_name">
                                        <Image publicId={product.imageUrl} alt={product.ProductName} style={{ maxWidth: '50px' }}>
                                            <Transformation width="50" crop="scale" />
                                        </Image>
                                    </CloudinaryContext>
                                </td>
                                <td>
                                    <button className="edit" onClick={() => handleEdit(product._id)}>Edit</button>
                                </td>
                                <td>
                                    <button className="delete" onClick={() => handleDelete(product._id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default Admin;
