import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from '../Components/Modal'; // Assuming Modal.js is correctly implemented and imported
import './orderlist.css'; 
// Adjust the CSS import as per your styling needs

function OrderList() {
    const [orders, setOrders] = useState([]);
    const [error, setError] = useState(null);
    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [selectedOrder, setSelectedOrder] = useState(null);
    const [isAcceptConfirmationOpen, setAcceptConfirmationOpen] = useState(false);
    const [isRejectConfirmationOpen, setRejectConfirmationOpen] = useState(false);

    useEffect(() => {
        // Fetch orders when component mounts
        axios.get('/api/orders') // Replace with your actual backend endpoint
            .then(response => {
                setOrders(response.data); // Set fetched orders to state
            })
            .catch(error => {
                console.error('Error fetching orders:', error);
                setError('Failed to fetch orders. Please try again later.');
            });
    }, []);

    const handleAccept = (orderId) => {
        // Example of handling accept button click
        // You can implement your logic here to update the order status, e.g., send a PATCH request
        console.log('Accepted order:', orderId);
    };

    const handleReject = (orderId) => {
        // Example of handling reject button click
        // You can implement your logic here to update the order status, e.g., send a PATCH request
        console.log('Rejected order:', orderId);
    };

    return (
        <div>
            <h1>Order List</h1>
            {error && <p>{error}</p>}
            <table>
                <thead>
                    <tr>
                        <th>Order ID</th>
                        <th>Name</th>
                        <th>Contact Number</th>
                        <th>Address</th>
                        <th>Deadline</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map(order => (
                        <tr key={order._id}>
                            <td>{order._id}</td>
                            <td>{order.Name}</td>
                            <td>{order.ContactNumber}</td>
                            <td>{order.Address}</td>
                            <td>{order.DeadLine}</td>
                            <td>
                                <button className="accept" onClick={() => handleAccept(order._id)}>Accept</button>
                                <button className="reject" onClick={() => handleReject(order._id)}>Reject</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Example Modal for Accept/Reject Confirmation */}
            <Modal isOpen={modalIsOpen} onClose={() => setModalIsOpen(false)}>
                <h2>Order Confirmation</h2>
                {selectedOrder && (
                    <div>
                        <p>Order ID: {selectedOrder._id}</p>
                        <p>Name: {selectedOrder.Name}</p>
                        <p>Contact Number: {selectedOrder.ContactNumber}</p>
                        <p>Address: {selectedOrder.Address}</p>
                        <p>Deadline: {selectedOrder.DeadLine}</p>
                        <p>Are you sure you want to accept/reject this order?</p>
                        <button onClick={() => {
                            handleAccept(selectedOrder._id);
                            setModalIsOpen(false);
                        }}>Accept</button>
                        <button onClick={() => {
                            handleReject(selectedOrder._id);
                            setModalIsOpen(false);
                        }}>Reject</button>
                    </div>
                )}
            </Modal>

            {/* Example Modals for Accept and Reject Confirmation */}
            {isAcceptConfirmationOpen && (
                <div className="confirmation-modal">
                    <div className="modal-content">
                        <h2>Confirm Accept</h2>
                        <p>Are you sure you want to accept this order?</p>
                        <button onClick={() => {
                            handleAccept(selectedOrder._id);
                            setAcceptConfirmationOpen(false);
                        }}>Yes</button>
                        <button onClick={() => setAcceptConfirmationOpen(false)}>No</button>
                    </div>
                </div>
            )}
            {isRejectConfirmationOpen && (
                <div className="confirmation-modal">
                    <div className="modal-content">
                        <h2>Confirm Reject</h2>
                        <p>Are you sure you want to reject this order?</p>
                        <button onClick={() => {
                            handleReject(selectedOrder._id);
                            setRejectConfirmationOpen(false);
                        }}>Yes</button>
                        <button onClick={() => setRejectConfirmationOpen(false)}>No</button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default OrderList;
