import './userdetails.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserTable = () => {
    const [users, setUsers] = useState([]);
    const [editingUser, setEditingUser] = useState(null);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = () => {
        axios.get('http://localhost:3003/user/getUser')
            .then(response => setUsers(response.data))
            .catch(error => {
                console.error('Error fetching users:', error);
                setError('An error occurred while fetching users. Please try again later.');
            });
    };

    const handleEditClick = (user) => {
        setEditingUser(user);
    };

    const handleDeleteClick = (userId) => {
        axios.delete(`http://localhost:3003/user/delete/${userId}`)
            .then(response => {
                fetchUsers();
            })
            .catch(error => {
                console.error('Error deleting user:', error);
                setError('An error occurred while deleting the user. Please try again later.');
            });
    };

    const handleEditChange = (e) => {
        const { name, value } = e.target;
        setEditingUser({ ...editingUser, [name]: value });
    };

    const handleEditSave = () => {
        axios.put(`http://localhost:3003/user/updateUser/${editingUser._UserId}`, editingUser)
            .then(response => {
                setEditingUser(null);
                fetchUsers();
            })
            .catch(error => {
                console.error('Error updating user:', error);
                setError('An error occurred while updating the user. Please try again later.');
            });
    };

    return (
        <div className="container-box">
            {error && <div className="error">Error: {error}</div>}
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Edit</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user._UserId}>
                            <td>
                                {editingUser && editingUser._UserId === user._UserId ? (
                                    <input
                                        type="text"
                                        name="Name"
                                        value={editingUser.Name}
                                        onChange={handleEditChange}
                                    />
                                ) : (
                                    user.Name
                                )}
                            </td>
                            <td>
                                {editingUser && editingUser._UserId === user._UserId ? (
                                    <input
                                        type="email"
                                        name="Email"
                                        value={editingUser.Email}
                                        onChange={handleEditChange}
                                    />
                                ) : (
                                    user.Email
                                )}
                            </td>
                            <td>
                                {editingUser && editingUser._UserId === user._UserId ? (
                                    <button className='save' onClick={handleEditSave}>Save</button>
                                ) : (
                                    <button className='edit' onClick={() => handleEditClick(user)}>Edit</button>
                                )}
                            </td>
                            <td>
                                <button className='delete' onClick={() => handleDeleteClick(user._UserId)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default UserTable;





