import React, { useState } from 'react';
import './Register.css';
import Navbar from '../Components/Navbar';
import { Link, useNavigate } from 'react-router-dom';

function RegistrationForm() {
  const [fullname, setFullName] = useState('');
  const [username, setUserName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmpassword, setConfirmPassword] = useState('');
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isRegistered, setIsRegistered] = useState(false); // New state for registration status
  const navigate = useNavigate();

  const validate = () => {
    // Your validation logic remains unchanged
    // ...
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = validate();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    setIsSubmitting(true);
    const result = { UserId: username, Name: fullname, Email: email, Password: password, Role: "user" };

    try {
      const response = await fetch('http://localhost:3003/usercreate/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(result),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setErrors({ apiError: errorData.message });
        setIsSubmitting(false);
        return;
      }

      // Registration successful
      setIsRegistered(true);
      setIsSubmitting(false);
    } catch (error) {
      console.error('Error:', error);
      setErrors({ apiError: 'An error occurred. Please try again later.' });
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <Navbar />
      <div className="Register-center">
        <h1>Registration Form</h1>
        {isRegistered ? ( // Display "Registered Successfully" if registration is successful
          <div className="success">Registered Successfully!</div>
        ) : (
          <form onSubmit={handleSubmit}>
            {/* Your form inputs and error handling */}
            {/* ... */}
          </form>
        )}
      </div>
    </>
  );
}

export default RegistrationForm;
