function LoginForm() {
  return (
    <div style={{ fontFamily: 'Arial, sans-serif', backgroundColor: '#f4f4f4' }}>
      <div style={{ maxWidth: '400px', margin: '0 auto', padding: '20px', backgroundColor: '#fff', borderRadius: '5px', boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)' }}>
        <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Login</h2>

        <form>
          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="username">Username:</label><br />
            <input type="text" id="username" name="username" style={{ width: '100%', padding: '10px', boxSizing: 'border-box', border: '1px solid #ccc', borderRadius: '5px' }} />
          </div>

          <div style={{ marginBottom: '15px' }}>
            <label htmlFor="password">Password:</label><br />
            <input type="password" id="password" name="password" style={{ width: '100%', padding: '10px', boxSizing: 'border-box', border: '1px solid #ccc', borderRadius: '5px' }} />
          </div>

          <button type="submit" style={{ width: '100%', padding: '10px', backgroundColor: '#4CAF50', color: '#fff', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>Login</button>
        </form>
      </div>
    </div>
  );
}

export default LoginForm;
