import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Login.css';
import Navbar from '../Components/Navbar';

function LoginForm() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validateForm = () => {
    const errors = {};
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!email) {
      errors.email = 'Email is required';
    } else if (!emailRegex.test(email)) {
      errors.email = 'Email is not valid';
    }

    if (!password) {
      errors.password = 'Password is required';
    } else if (password.length < 6) {
      errors.password = 'Password must be at least 6 characters long';
    }

    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    try {
      const response = await axios('http://localhost:3003/usercreate/login', {
        method: 'POST',
        headers: {
          Accept :"application/json",
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });
      console.log(response);  
      const data = await response.json();
      const token = data.token; // Assuming your token is returned in the response

      if (!response.ok) {
        throw new Error('An error occurred while logging in.');
      }

      if (data.Role === 'user') {
        alert(`${data.Name} login successful`);
        localStorage.setItem('token', token);
        navigate('/'); // Redirect to the home page for regular users
      } else if (data.Role === 'Admin') {
        alert(`${data.Name} login successful`);
        localStorage.setItem('token', token);
        navigate('/Dashboard'); // Redirect to the admin dashboard page
      } else {
        alert('Invalid role.');
      }

    } catch (error) {
      console.error('Error logging in:', error);
      alert('An error occurred while logging in. Please try again later.');
    }
  };

  return (
    <>
      <Navbar />
      <div className='login-body'>
        <div className='log'>
          <div className="Login-center">
            <h1>Login</h1>
            <form method="post" onSubmit={handleSubmit}>
              <div className="txt_field">
                <input
                  type="email"
                  required
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
                <span></span>
                <label>Email</label>
                {errors.email && <span className="error">{errors.email}</span>}
              </div>
              <div className="txt_field">
                <input
                  type="password"
                  required
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
                <span></span>
                <label>Password</label>
                {errors.password && <span className="error">{errors.password}</span>}
              </div>
              <div className="pass">Forgot Password?</div>
              <input type="submit" value="Login" />
              <div className="signup_link">
                Not a member? <a href="Register">Signup</a>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

export default LoginForm;
